import { useState, useCallback } from "react";
import { useToast } from "@/hooks/use-toast";

interface Address {
  formatted_address: string;
  city: string;
  country: string;
  place_id?: string;
}

interface UseGoogleMapsReturn {
  geocodeLocation: (lat: number, lng: number) => Promise<Address | null>;
  searchPlaces: (query: string, location?: { lat: number; lng: number }) => Promise<google.maps.places.PlaceResult[]>;
  isLoading: boolean;
  error: string | null;
}

export function useGoogleMaps(): UseGoogleMapsReturn {
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const { toast } = useToast();

  const geocodeLocation = useCallback(async (lat: number, lng: number): Promise<Address | null> => {
    if (!window.google) {
      setError("Google Maps non disponible");
      return null;
    }

    setIsLoading(true);
    setError(null);

    try {
      const geocoder = new google.maps.Geocoder();
      const response = await geocoder.geocode({
        location: { lat, lng },
        language: 'fr',
        region: 'BF', // Burkina Faso
        componentRestrictions: {
          country: 'BF' // Restriction au Burkina Faso
        }
      });

      if (response.results && response.results.length > 0) {
        const result = response.results[0];
        const cityComponent = result.address_components?.find(
          component => component.types.includes('locality') || 
                      component.types.includes('administrative_area_level_2')
        );

        const countryComponent = result.address_components?.find(
          component => component.types.includes('country')
        );

        return {
          formatted_address: result.formatted_address,
          city: cityComponent?.long_name || 'Ville inconnue',
          country: countryComponent?.long_name || 'Burkina Faso',
          place_id: result.place_id,
        };
      }

      return null;
    } catch (err) {
      const errorMessage = err instanceof Error ? err.message : 'Erreur de géocodage';
      setError(errorMessage);
      toast({
        title: "Erreur de localisation",
        description: errorMessage,
        variant: "destructive",
      });
      return null;
    } finally {
      setIsLoading(false);
    }
  }, [toast]);

  const searchPlaces = useCallback(async (
    query: string, 
    location?: { lat: number; lng: number }
  ): Promise<google.maps.places.PlaceResult[]> => {
    if (!window.google) {
      setError("Google Maps non disponible");
      return [];
    }

    setIsLoading(true);
    setError(null);

    try {
      const service = new google.maps.places.PlacesService(
        document.createElement('div')
      );

      // Centre par défaut sur Ouagadougou si aucune localisation fournie
      const searchLocation = location || { lat: 12.3714, lng: -1.5197 };

      return new Promise((resolve, reject) => {
        const request: google.maps.places.TextSearchRequest = {
          query: `${query} Burkina Faso`,
          location: new google.maps.LatLng(searchLocation.lat, searchLocation.lng),
          radius: 50000, // 50km de rayon
          language: 'fr',
          region: 'BF',
          type: 'establishment'
        };

        service.textSearch(request, (results, status) => {
          if (status === google.maps.places.PlacesServiceStatus.OK && results) {
            resolve(results);
          } else {
            reject(new Error(`Recherche échouée: ${status}`));
          }
        });
      });
    } catch (err) {
      const errorMessage = err instanceof Error ? err.message : 'Erreur de recherche';
      setError(errorMessage);
      toast({
        title: "Erreur de recherche",
        description: errorMessage,
        variant: "destructive",
      });
      return [];
    } finally {
      setIsLoading(false);
    }
  }, [toast]);

  return {
    geocodeLocation,
    searchPlaces,
    isLoading,
    error,
  };
}

