import {
  type User, type InsertUser,
  type Category, type InsertCategory,
  type Product, type InsertProduct, type ProductWithDetails,
  type PriceUpdate, type InsertPriceUpdate,
  type BarterRequest, type InsertBarterRequest, type BarterRequestWithDetails,
  type Message, type InsertMessage, type MessageWithDetails,
  type DailyDeal, type InsertDailyDeal, type DailyDealWithProduct
} from "@shared/schema";
import { randomUUID } from "crypto";

export interface IStorage {
  // Users
  getUser(id: string): Promise<User | undefined>;
  getUserByUsername(username: string): Promise<User | undefined>;
  getUserByFirebaseUid(firebaseUid: string): Promise<User | undefined>;
  createUser(user: InsertUser): Promise<User>;

  // Categories
  getCategories(): Promise<Category[]>;
  createCategory(category: InsertCategory): Promise<Category>;

  // Products
  getProducts(params?: { categoryId?: string; vendorId?: string; latitude?: number; longitude?: number; radius?: number }): Promise<ProductWithDetails[]>;
  getProduct(id: string): Promise<ProductWithDetails | undefined>;
  createProduct(product: InsertProduct): Promise<Product>;
  updateProduct(id: string, updates: Partial<InsertProduct>): Promise<Product | undefined>;

  // Price Updates
  createPriceUpdate(priceUpdate: InsertPriceUpdate): Promise<PriceUpdate>;
  getProductPriceUpdates(productId: string): Promise<PriceUpdate[]>;

  // Barter Requests
  getBarterRequests(userId: string): Promise<BarterRequestWithDetails[]>;
  createBarterRequest(barterRequest: InsertBarterRequest): Promise<BarterRequest>;
  updateBarterRequest(id: string, status: string): Promise<BarterRequest | undefined>;

  // Messages
  getConversation(userId1: string, userId2: string, productId?: string): Promise<MessageWithDetails[]>;
  createMessage(message: InsertMessage): Promise<Message>;

  // Daily Deals
  getDailyDeals(latitude?: number, longitude?: number): Promise<DailyDealWithProduct[]>;
  createDailyDeal(dailyDeal: InsertDailyDeal): Promise<DailyDeal>;
}

export class MemStorage implements IStorage {
  private users: Map<string, User> = new Map();
  private categories: Map<string, Category> = new Map();
  private products: Map<string, Product> = new Map();
  private priceUpdates: Map<string, PriceUpdate> = new Map();
  private barterRequests: Map<string, BarterRequest> = new Map();
  private messages: Map<string, Message> = new Map();
  private dailyDeals: Map<string, DailyDeal> = new Map();

  constructor() {
    this.seedInitialData();
  }

  private seedInitialData() {
    // Seed categories
    const categories = [
      { name: "Légumes", icon: "fas fa-seedling", color: "from-green-400 to-green-600" },
      { name: "Poissons", icon: "fas fa-fish", color: "from-blue-400 to-blue-600" },
      { name: "Tissus", icon: "fas fa-cut", color: "from-purple-400 to-purple-600" },
      { name: "Fruits", icon: "fas fa-apple-alt", color: "from-orange-400 to-orange-600" },
    ];

    categories.forEach(cat => {
      const id = randomUUID();
      this.categories.set(id, { id, ...cat });
    });

    // Seed users (vendors)
    const vendors = [
      { username: "fatou_vendor", password: "password", fullName: "Fatou Diop", phone: "+221 77 123 4567", location: "Marché Sandaga, Dakar", isVendor: true },
      { username: "mamadou_fish", password: "password", fullName: "Mamadou Ndiaye", phone: "+221 76 234 5678", location: "Marché au poisson, Dakar", isVendor: true },
      { username: "khadija_textile", password: "password", fullName: "Khadija Ba", phone: "+221 75 345 6789", location: "Marché HLM, Dakar", isVendor: true },
    ];

    vendors.forEach(vendor => {
      const id = randomUUID();
      this.users.set(id, { id, ...vendor, createdAt: new Date() });
    });

    // Seed products
    const categoryArray = Array.from(this.categories.values());
    const vendorArray = Array.from(this.users.values());

    const sampleProducts = [
      {
        name: "Manioc Bio",
        description: "Manioc frais de la ferme de Mbour",
        categoryId: categoryArray[0].id, // Légumes
        vendorId: vendorArray[0].id,
        price: "1200",
        unit: "kg",
        imageUrl: "https://pixabay.com/get/g76f4cb83b6f671484cdce92634e769fea5e1b5588b16938a281758c2ae5534e581cddfa2158f3e6adcf966cbb9d5b323e626141aca0baa9ccedecb19ba106fd8_1280.jpg",
        latitude: "14.7167",
        longitude: "-17.4677",
        rating: "4.6",
        reviewCount: 12
      },
      {
        name: "Poisson Thiof",
        description: "Thiof frais pêché ce matin",
        categoryId: categoryArray[1].id, // Poissons
        vendorId: vendorArray[1].id,
        price: "2800",
        unit: "kg",
        imageUrl: "https://images.unsplash.com/photo-1544943910-4c1dc44aab44?ixlib=rb-4.0.3&auto=format&fit=crop&w=100&h=100",
        latitude: "14.7167",
        longitude: "-17.4677",
        rating: "4.8",
        reviewCount: 25
      },
      {
        name: "Wax Premium",
        description: "Tissu wax de haute qualité, 12 motifs disponibles",
        categoryId: categoryArray[2].id, // Tissus
        vendorId: vendorArray[2].id,
        price: "8500",
        unit: "m",
        imageUrl: "https://images.unsplash.com/photo-1582582621959-48d27397dc69?ixlib=rb-4.0.3&auto=format&fit=crop&w=400&h=200",
        latitude: "14.7167",
        longitude: "-17.4677",
        rating: "4.9",
        reviewCount: 28
      }
    ];

    sampleProducts.forEach(product => {
      const id = randomUUID();
      this.products.set(id, {
        id,
        ...product,
        isAvailable: true,
        createdAt: new Date(),
        updatedAt: new Date()
      });
    });

    // Seed daily deals
    const productArray = Array.from(this.products.values());
    if (productArray.length > 0) {
      const dealId = randomUUID();
      const product = productArray[0]; // Manioc
      this.dailyDeals.set(dealId, {
        id: dealId,
        productId: product.id,
        originalPrice: "1500",
        dealPrice: "1200",
        discountPercentage: 20,
        validUntil: new Date(Date.now() + 24 * 60 * 60 * 1000), // 24 hours
        createdAt: new Date()
      });
    }
  }

  private calculateDistance(lat1: number, lon1: number, lat2: number, lon2: number): number {
    const R = 6371; // Earth's radius in km
    const dLat = (lat2 - lat1) * Math.PI / 180;
    const dLon = (lon2 - lon1) * Math.PI / 180;
    const a =
      Math.sin(dLat / 2) * Math.sin(dLat / 2) +
      Math.cos(lat1 * Math.PI / 180) * Math.cos(lat2 * Math.PI / 180) *
      Math.sin(dLon / 2) * Math.sin(dLon / 2);
    const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1 - a));
    return R * c;
  }

  async getUser(id: string): Promise<User | undefined> {
    return this.users.get(id);
  }

  async getUserByUsername(username: string): Promise<User | undefined> {
    return Array.from(this.users.values()).find(
      (user) => user.username === username,
    );
  }

  async getUserByFirebaseUid(firebaseUid: string): Promise<User | undefined> {
    return Array.from(this.users.values()).find(
      (user) => user.firebaseUid === firebaseUid,
    );
  }


  async createUser(userData: any): Promise<User> {
    const id = randomUUID();
    const user: User = {
      id,
      ...userData,
      createdAt: new Date(),
    };
    this.users.set(id, user);
    return user;
  }

  async authenticateUser(username: string, password: string): Promise<User | undefined> {
    const user = await this.getUserByUsername(username);
    if (user && user.password === password) {
      return user;
    }
    return undefined;
  }

  async updateUser(id: string, updates: Partial<User>): Promise<User | undefined> {
    const user = this.users.get(id);
    if (!user) return undefined;

    const updatedUser = { ...user, ...updates, updatedAt: new Date() };
    this.users.set(id, updatedUser);
    return updatedUser;
  }



  async getCategories(): Promise<Category[]> {
    return Array.from(this.categories.values());
  }

  async createCategory(insertCategory: InsertCategory): Promise<Category> {
    const id = randomUUID();
    const category: Category = { ...insertCategory, id };
    this.categories.set(id, category);
    return category;
  }

  async getProducts(params?: { categoryId?: string; vendorId?: string; latitude?: number; longitude?: number; radius?: number }): Promise<ProductWithDetails[]> {
    let products = Array.from(this.products.values());

    if (params?.categoryId) {
      products = products.filter(p => p.categoryId === params.categoryId);
    }

    if (params?.vendorId) {
      products = products.filter(p => p.vendorId === params.vendorId);
    }

    if (params?.latitude && params?.longitude && params?.radius) {
      products = products.filter(p => {
        if (!p.latitude || !p.longitude) return false;
        const distance = this.calculateDistance(
          params.latitude!,
          params.longitude!,
          parseFloat(p.latitude),
          parseFloat(p.longitude)
        );
        return distance <= params.radius!;
      });
    }

    return products.map(product => this.enrichProduct(product, params?.latitude, params?.longitude));
  }

  async getProduct(id: string): Promise<ProductWithDetails | undefined> {
    const product = this.products.get(id);
    if (!product) return undefined;
    return this.enrichProduct(product);
  }

  private enrichProduct(product: Product, userLat?: number, userLon?: number): ProductWithDetails {
    const category = product.categoryId ? this.categories.get(product.categoryId) : undefined;
    const vendor = product.vendorId ? this.users.get(product.vendorId) : undefined;

    let distance: number | undefined;
    if (userLat && userLon && product.latitude && product.longitude) {
      distance = this.calculateDistance(userLat, userLon, parseFloat(product.latitude), parseFloat(product.longitude));
    }

    const priceUpdates = Array.from(this.priceUpdates.values())
      .filter(update => update.productId === product.id)
      .sort((a, b) => new Date(b.createdAt!).getTime() - new Date(a.createdAt!).getTime());

    const lastPriceUpdate = priceUpdates[0];

    return {
      ...product,
      category,
      vendor,
      distance,
      lastPriceUpdate
    };
  }

  async createProduct(insertProduct: InsertProduct): Promise<Product> {
    const id = randomUUID();
    const product: Product = {
      ...insertProduct,
      id,
      isAvailable: true,
      rating: "0",
      reviewCount: 0,
      createdAt: new Date(),
      updatedAt: new Date()
    };
    this.products.set(id, product);
    return product;
  }

  async updateProduct(id: string, updates: Partial<InsertProduct>): Promise<Product | undefined> {
    const product = this.products.get(id);
    if (!product) return undefined;

    const updated = { ...product, ...updates, updatedAt: new Date() };
    this.products.set(id, updated);
    return updated;
  }

  async createPriceUpdate(insertPriceUpdate: InsertPriceUpdate): Promise<PriceUpdate> {
    const id = randomUUID();
    const priceUpdate: PriceUpdate = {
      ...insertPriceUpdate,
      id,
      verified: false,
      createdAt: new Date()
    };
    this.priceUpdates.set(id, priceUpdate);

    // Update product price if this is a verified update
    if (insertPriceUpdate.productId) {
      const product = this.products.get(insertPriceUpdate.productId);
      if (product) {
        const updatedProduct = { ...product, price: insertPriceUpdate.newPrice, updatedAt: new Date() };
        this.products.set(insertPriceUpdate.productId, updatedProduct);
      }
    }

    return priceUpdate;
  }

  async getProductPriceUpdates(productId: string): Promise<PriceUpdate[]> {
    return Array.from(this.priceUpdates.values())
      .filter(update => update.productId === productId)
      .sort((a, b) => new Date(b.createdAt!).getTime() - new Date(a.createdAt!).getTime());
  }

  async getBarterRequests(userId: string): Promise<BarterRequestWithDetails[]> {
    const requests = Array.from(this.barterRequests.values())
      .filter(request => request.requesterId === userId ||
        (request.offerProductId && this.products.get(request.offerProductId)?.vendorId === userId));

    return requests.map(request => ({
      ...request,
      requester: request.requesterId ? this.users.get(request.requesterId) : undefined,
      offerProduct: request.offerProductId ? this.enrichProduct(this.products.get(request.offerProductId)!) : undefined,
      requestedProduct: request.requestedProductId ? this.enrichProduct(this.products.get(request.requestedProductId)!) : undefined,
    }));
  }

  async createBarterRequest(insertBarterRequest: InsertBarterRequest): Promise<BarterRequest> {
    const id = randomUUID();
    const barterRequest: BarterRequest = {
      ...insertBarterRequest,
      id,
      status: "pending",
      createdAt: new Date()
    };
    this.barterRequests.set(id, barterRequest);
    return barterRequest;
  }

  async updateBarterRequest(id: string, status: string): Promise<BarterRequest | undefined> {
    const request = this.barterRequests.get(id);
    if (!request) return undefined;

    const updated = { ...request, status };
    this.barterRequests.set(id, updated);
    return updated;
  }

  async getConversation(userId1: string, userId2: string, productId?: string): Promise<MessageWithDetails[]> {
    let messages = Array.from(this.messages.values())
      .filter(msg =>
        (msg.senderId === userId1 && msg.receiverId === userId2) ||
        (msg.senderId === userId2 && msg.receiverId === userId1)
      );

    if (productId) {
      messages = messages.filter(msg => msg.productId === productId);
    }

    return messages
      .sort((a, b) => new Date(a.createdAt!).getTime() - new Date(b.createdAt!).getTime())
      .map(message => ({
        ...message,
        sender: message.senderId ? this.users.get(message.senderId) : undefined,
        receiver: message.receiverId ? this.users.get(message.receiverId) : undefined,
        product: message.productId ? this.enrichProduct(this.products.get(message.productId)!) : undefined,
      }));
  }

  async createMessage(insertMessage: InsertMessage): Promise<Message> {
    const id = randomUUID();
    const message: Message = {
      ...insertMessage,
      id,
      isVoiceMessage: false,
      createdAt: new Date()
    };
    this.messages.set(id, message);
    return message;
  }

  async getDailyDeals(latitude?: number, longitude?: number): Promise<DailyDealWithProduct[]> {
    const deals = Array.from(this.dailyDeals.values())
      .filter(deal => new Date(deal.validUntil) > new Date()); // Only active deals

    return deals.map(deal => ({
      ...deal,
      product: deal.productId ? this.enrichProduct(this.products.get(deal.productId)!, latitude, longitude) : undefined,
    }));
  }

  async createDailyDeal(insertDailyDeal: InsertDailyDeal): Promise<DailyDeal> {
    const id = randomUUID();
    const dailyDeal: DailyDeal = {
      ...insertDailyDeal,
      id,
      createdAt: new Date()
    };
    this.dailyDeals.set(id, dailyDeal);
    return dailyDeal;
  }
}

export const storage = new MemStorage();

