export default function Notifications() {
  // Mock notifications - in real app, fetch from API
  const notifications = [
    {
      id: "1",
      type: "urgence",
      title: "Nouveau signalement SOS",
      description: "Un signalement urgent a été créé dans votre zone",
      time: "Il y a 5 minutes",
      read: false,
    },
    {
