import React, { useEffect, useState } from 'react';
import { Button } from '@/components/ui/button';
import { useToast } from '@/hooks/use-toast';

interface ReplitUser {
  id: string;
  name: string;
  username: string;
  roles: string[];
}

export function ReplitAuth() {
  const [user, setUser] = useState<ReplitUser | null>(null);
  const [loading, setLoading] = useState(true);
  const { toast } = useToast();

  useEffect(() => {
    checkAuth();
  }, []);

  const checkAuth = async () => {
    try {
      const response = await fetch('/api/auth/me');
      if (response.ok) {
        const userData = await response.json();
        setUser(userData);
      }
    } catch (error) {
      console.error('Erreur auth:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleLogin = () => {
    // Recharger la page après authentification
    window.location.reload();
  };

  if (loading) {
    return <div>Chargement...</div>;
  }

  if (user) {
    return (
      <div className="text-center">
        <h2>Bonjour, {user.name}!</h2>
        <p>ID utilisateur: {user.id}</p>
      </div>
    );
  }

  return (
    <div className="text-center">
      <p>Veuillez vous connecter</p>
      <div>
        <script
          dangerouslySetInnerHTML={{
            __html: `
              (function() {
                const script = document.createElement('script');
                script.src = 'https://auth.util.repl.co/script.js';
                script.setAttribute('authed', 'location.reload()');
                document.body.appendChild(script);
              })();
            `
          }}
        />
      </div>
      <Button onClick={handleLogin} className="mt-4">
        Se connecter avec Replit
      </Button>
    </div>
  );
}

