import { useState } from "react";
import { useQuery } from "@tanstack/react-query";
import { Header } from "@/components/layout/header";
import { CategoryGrid } from "@/components/product/category-grid";
import { ProductCard } from "@/components/product/product-card";
import { DailyDeals } from "@/components/deals/daily-deals";
import { BarterCard } from "@/components/bartering/barter-card";
import { VoiceModal } from "@/components/voice/voice-modal";
import { Button } from "@/components/ui/button";
import { Skeleton } from "@/components/ui/skeleton";
import { Mic, Plus } from "lucide-react";
import { useGeolocation } from "@/hooks/use-geolocation";
import { useToast } from "@/hooks/use-toast";
import type { ProductWithDetails } from "@shared/schema";
import { useLocation } from "wouter";

export default function Home() {
  const [, setLocation] = useLocation();
  const [selectedCategory, setSelectedCategory] = useState<string>("");
  const [showVoiceModal, setShowVoiceModal] = useState(false);
  const [searchQuery, setSearchQuery] = useState("");
  const { latitude, longitude } = useGeolocation();
  const { toast } = useToast();

  const { data: products, isLoading } = useQuery({
    queryKey: ["/api/products", selectedCategory, latitude, longitude],
    enabled: latitude !== null && longitude !== null,
  });

  const { data: searchResults } = useQuery({
    queryKey: ["/api/search", searchQuery, latitude, longitude],
    enabled: searchQuery.length > 0 && latitude !== null && longitude !== null,
  });

  const displayProducts = searchQuery ? searchResults : products;

  const handleSearch = (query: string) => {
    setSearchQuery(query);
  };

  const handleCategorySelect = (categoryId: string) => {
    setSelectedCategory(categoryId);
    setSearchQuery("");
  };

  const handleBarter = (product: ProductWithDetails) => {
    toast({
      title: "Troc initié",
      description: `Troc proposé pour ${product.name}`,
    });
    setLocation("/bartering");
  };

  const handleVoiceChat = (product: ProductWithDetails) => {
    setShowVoiceModal(true);
    toast({
      title: "Chat vocal",
      description: `Négociation vocale pour ${product.name}`,
    });
  };

  const handleShare = (product: ProductWithDetails) => {
    if (navigator.share) {
      navigator.share({
        title: product.name,
        text: `${product.name} à ${parseInt(product.price).toLocaleString()} CFA/${product.unit}`,
        url: window.location.href,
      });
    } else {
      toast({
        title: "Produit partagé",
        description: "Lien copié dans le presse-papier",
      });
    }
  };

  const handleVoiceTranscript = (transcript: string) => {
    handleSearch(transcript);
    setShowVoiceModal(false);
  };

  const handleQuickVoice = () => {
    setShowVoiceModal(true);
  };

  return (
    <div className="min-h-screen bg-gray-50">
      <Header onSearch={handleSearch} />

      <div className="pb-20">
        <DailyDeals
          onBarter={handleBarter}
          onChat={(productId) => setLocation(`/messages?product=${productId}`)}
        />

        <CategoryGrid onCategorySelect={handleCategorySelect} />

        <div className="px-4">
          <div className="flex items-center justify-between mb-4">
            <h2 className="text-lg font-semibold">
              {searchQuery ? "Résultats de recherche" : "Produits Près de Vous"}
            </h2>
            <Button
              variant="ghost"
              className="text-[#E67E22] text-sm font-medium"
              onClick={() => setLocation("/search")}
            >
              Tout voir
            </Button>
          </div>

          {isLoading ? (
            <div className="space-y-4">
              {Array.from({ length: 3 }).map((_, i) => (
                <div key={i} className="border rounded-2xl overflow-hidden">
                  <Skeleton className="w-full h-40" />
                  <div className="p-4 space-y-2">
                    <Skeleton className="h-4 w-3/4" />
                    <Skeleton className="h-3 w-1/2" />
                    <Skeleton className="h-8 w-full" />
                  </div>
                </div>
              ))}
            </div>
          ) : (
            <div>
              {displayProducts?.map((product: ProductWithDetails) => (
                <ProductCard
                  key={product.id}
                  product={product}
                  onBarter={handleBarter}
                  onVoiceChat={handleVoiceChat}
                  onShare={handleShare}
                />
              ))}

              {displayProducts?.length === 0 && (
                <div className="text-center py-8">
                  <p className="text-gray-500 mb-2">
                    {searchQuery ? "Aucun résultat trouvé" : "Aucun produit disponible"}
                  </p>
                  {searchQuery && (
                    <Button
                      variant="outline"
                      onClick={() => setSearchQuery("")}
                    >
                      Réinitialiser la recherche
                    </Button>
                  )}
                </div>
              )}
            </div>
          )}
        </div>

        <BarterCard />
      </div>

      {/* Floating Action Button */}
      <Button
        size="icon"
        className="fixed bottom-24 right-4 w-14 h-14 bg-[#E67E22] hover:bg-[#D35400] text-white rounded-full shadow-lg z-40"
        onClick={handleQuickVoice}
      >
        <Mic className="w-6 h-6" />
      </Button>

      {/* Action Buttons */}
      <div className="p-4 space-y-3">
        <Button
          onClick={() => setLocation("/sell")}
          className="w-full bg-[#27AE60] hover:bg-[#219A52] text-white font-medium py-3"
        >
          <Plus className="w-5 h-5 mr-2" />
          Vendre un produit
        </Button>
        <Button
          onClick={() => setShowVoiceModal(true)}
          className="w-full bg-[#E67E22] hover:bg-[#D35400] text-white font-medium py-3"
        >
          <Mic className="w-5 h-5 mr-2" />
          Recherche vocale
        </Button>
      </div>

      <VoiceModal
        open={showVoiceModal}
        onOpenChange={setShowVoiceModal}
        onTranscript={handleVoiceTranscript}
      />
    </div>
  );
}

