import { Dialog, DialogContent } from "@/components/ui/dialog";
import { Button } from "@/components/ui/button";
import { useVoiceRecognition } from "@/hooks/use-voice-recognition";
import { Mic, MicOff, Volume2 } from "lucide-react";
import { useEffect } from "react";

interface VoiceModalProps {
  open: boolean;
  onOpenChange: (open: boolean) => void;
  onTranscript?: (text: string) => void;
}

export function VoiceModal({ open, onOpenChange, onTranscript }: VoiceModalProps) {
  const { 
    isListening, 
    transcript, 
    error, 
    isSupported, 
    startListening, 
    stopListening, 
    resetTranscript 
  } = useVoiceRecognition();

  useEffect(() => {
    if (open && isSupported) {
      resetTranscript();
      startListening();
    } else {
      stopListening();
    }
  }, [open, isSupported]);

  useEffect(() => {
    if (transcript && onTranscript) {
      onTranscript(transcript);
    }
  }, [transcript, onTranscript]);

  const handleClose = () => {
    stopListening();
    onOpenChange(false);
  };

  if (!isSupported) {
    return (
      <Dialog open={open} onOpenChange={onOpenChange}>
        <DialogContent className="sm:max-w-md">
          <div className="text-center p-6">
            <Volume2 className="w-12 h-12 text-gray-400 mx-auto mb-4" />
            <h3 className="text-lg font-semibold mb-2">Reconnaissance vocale non supportée</h3>
            <p className="text-sm text-gray-600 mb-4">
              Votre navigateur ne supporte pas la reconnaissance vocale. 
              Essayez avec Chrome ou Safari.
            </p>
            <Button onClick={handleClose} className="w-full">
              Fermer
            </Button>
          </div>
        </DialogContent>
      </Dialog>
    );
  }

  return (
    <Dialog open={open} onOpenChange={onOpenChange}>
      <DialogContent className="sm:max-w-md">
        <div className="text-center p-6">
          <div className={`w-20 h-20 rounded-full flex items-center justify-center mx-auto mb-4 ${
            isListening 
              ? 'bg-gradient-to-br from-orange-400 to-amber-500 animate-pulse' 
              : 'bg-gray-200'
          }`}>
            {isListening ? (
              <Mic className="w-8 h-8 text-white" />
            ) : (
              <MicOff className="w-8 h-8 text-gray-500" />
            )}
          </div>

          <h3 className="text-lg font-semibold mb-2">
            {isListening ? "Écoute en cours..." : "Reconnaissance vocale"}
          </h3>

          {transcript && (
            <div className="bg-gray-50 rounded-lg p-3 mb-4">
              <p className="text-sm text-gray-700">"{transcript}"</p>
            </div>
          )}

          {error && (
            <div className="bg-red-50 rounded-lg p-3 mb-4">
              <p className="text-sm text-red-600">Erreur: {error}</p>
            </div>
          )}

          <div className="space-y-2">
            {isListening ? (
              <Button 
                onClick={stopListening}
                className="w-full bg-red-500 hover:bg-red-600 text-white"
              >
                <MicOff className="w-4 h-4 mr-2" />
                Arrêter
              </Button>
            ) : (
              <Button 
                onClick={startListening}
                className="w-full bg-gradient-to-r from-orange-400 to-amber-500 text-white"
              >
                <Mic className="w-4 h-4 mr-2" />
                Commencer
              </Button>
            )}

