import { useState, useEffect } from 'react';
import { 
  signInWithPopup, 
  signOut as firebaseSignOut, 
  onAuthStateChanged,
  User as FirebaseUser
} from 'firebase/auth';
import { auth, googleProvider, facebookProvider } from '@/lib/firebase';
import { useToast } from '@/hooks/use-toast';

export interface AuthUser {
  uid: string;
  email: string | null;
  displayName: string | null;
  photoURL: string | null;
  provider: string;
}

export function useFirebaseAuth() {
  const [user, setUser] = useState<AuthUser | null>(null);
  const [loading, setLoading] = useState(true);
  const { toast } = useToast();

  useEffect(() => {
    const unsubscribe = onAuthStateChanged(auth, (firebaseUser) => {
      if (firebaseUser) {
        const authUser: AuthUser = {
          uid: firebaseUser.uid,
          email: firebaseUser.email,
          displayName: firebaseUser.displayName,
          photoURL: firebaseUser.photoURL,
          provider: firebaseUser.providerData[0]?.providerId || 'unknown'
        };
        setUser(authUser);
      } else {
        setUser(null);
      }
      setLoading(false);
    });

    return () => unsubscribe();
  }, []);

  const signInWithGoogle = async () => {
    try {
      setLoading(true);
      const result = await signInWithPopup(auth, googleProvider);

      // Créer ou mettre à jour l'utilisateur dans votre base de données
      await createOrUpdateUser(result.user, 'google');

      toast({
        title: "Connexion réussie",
        description: `Bienvenue ${result.user.displayName}!`,
      });
    } catch (error: any) {
      console.error('Erreur de connexion Google:', error);
      toast({
        title: "Erreur de connexion",
        description: "Impossible de se connecter avec Google",
        variant: "destructive",
      });
    } finally {
      setLoading(false);
    }
  };

  const signInWithFacebook = async () => {
    try {
      setLoading(true);
      const result = await signInWithPopup(auth, facebookProvider);

      // Créer ou mettre à jour l'utilisateur dans votre base de données
      await createOrUpdateUser(result.user, 'facebook');

      toast({
        title: "Connexion réussie",
        description: `Bienvenue ${result.user.displayName}!`,
      });
    } catch (error: any) {
      console.error('Erreur de connexion Facebook:', error);
      toast({
        title: "Erreur de connexion",
        description: "Impossible de se connecter avec Facebook",
        variant: "destructive",
      });
    } finally {
      setLoading(false);
    }
  };

  const signOut = async () => {
    try {
      await firebaseSignOut(auth);
      toast({
        title: "Déconnexion",
        description: "Vous avez été déconnecté avec succès",
      });
    } catch (error) {
      console.error('Erreur de déconnexion:', error);
      toast({
        title: "Erreur",
        description: "Erreur lors de la déconnexion",
        variant: "destructive",
      });
    }
  };

  const createOrUpdateUser = async (firebaseUser: FirebaseUser, provider: string) => {
    try {
      const userData = {
        username: firebaseUser.email?.split('@')[0] || firebaseUser.uid,
        fullName: firebaseUser.displayName || 'Utilisateur',
        email: firebaseUser.email,
        phone: firebaseUser.phoneNumber || '',
        location: '',
        isVendor: false,
        provider,
        firebaseUid: firebaseUser.uid,
        photoURL: firebaseUser.photoURL
      };

      const response = await fetch('/api/auth/social-login', {
        method: 'POST',
        headers: { 
          'Content-Type': 'application/json',
          'X-Firebase-UID': firebaseUser.uid
        },
        body: JSON.stringify(userData),
      });

      if (!response.ok) {
        throw new Error('Erreur lors de la création/mise à jour de l\'utilisateur');
      }
    } catch (error) {
      console.error('Erreur lors de la création/mise à jour de l\'utilisateur:', error);
    }
  };

  return {
    user,
    loading,
    signInWithGoogle,
    signInWithFacebook,
    signOut
  };
}

