import { useState } from "react";
import { Card, CardContent, CardHeader } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Badge } from "@/components/ui/badge";
import { MessageCircle, Send, Mic, Phone, Video, ArrowLeft } from "lucide-react";
import { useLocation } from "wouter";

// Mock conversations data
const mockConversations = [
  {
    id: "1",
    participant: {
      name: "Fatou Diop",
      avatar: "https://images.unsplash.com/photo-1494790108755-2616c6791129?w=50&h=50&fit=crop&crop=face",
      isOnline: true,
    },
    product: {
      name: "Manioc Bio",
      price: "1200 CFA/kg",
      image: "https://pixabay.com/get/g76f4cb83b6f671484cdce92634e769fea5e1b5588b16938a281758c2ae5534e581cddfa2158f3e6adcf966cbb9d5b323e626141aca0baa9ccedecb19ba106fd8_1280.jpg",
    },
    lastMessage: "Oui, le manioc est disponible. Vous pouvez venir le voir demain matin.",
    timestamp: "il y a 5 min",
    unread: 2,
  },
  {
    id: "2",
    participant: {
      name: "Mamadou Ndiaye",
      avatar: "https://images.unsplash.com/photo-1472099645785-5658abf4ff4e?w=50&h=50&fit=crop&crop=face",
      isOnline: false,
    },
    product: {
      name: "Poisson Thiof",
      price: "2800 CFA/kg",
      image: "https://images.unsplash.com/photo-1544943910-4c1dc44aab44?w=50&h=50&fit=crop&crop=face",
    },
    lastMessage: "Le poisson est très frais, pêché ce matin. Vous voulez combien de kg?",
    timestamp: "il y a 2h",
    unread: 0,
  },
];

const mockMessages = [
  {
    id: "1",
    senderId: "other",
    content: "Bonjour! Je vois que vous vendez du manioc bio. Est-ce qu'il est toujours disponible?",
    timestamp: "14:30",
    isVoice: false,
  },
  {
    id: "2",
    senderId: "me",
    content: "Bonjour! Oui, j'ai encore du stock. Combien vous en faut-il?",
    timestamp: "14:32",
    isVoice: false,
  },
  {
    id: "3",
    senderId: "other",
    content: "J'aimerais prendre 3kg. Le prix est toujours à 1200 CFA/kg?",
    timestamp: "14:33",
    isVoice: false,
  },
  {
    id: "4",
    senderId: "me",
    content: "C'est parfait! Oui, c'est toujours le même prix. Quand voulez-vous venir le récupérer?",
    timestamp: "14:35",
    isVoice: false,
  },
  {
    id: "5",
    senderId: "other",
    content: "Oui, le manioc est disponible. Vous pouvez venir le voir demain matin.",
    timestamp: "14:40",
    isVoice: true,
    duration: "0:15",
  },
];

export default function MessagesPage() {
  const [, setLocation] = useLocation();
  const [selectedConversation, setSelectedConversation] = useState<string | null>(null);
  const [newMessage, setNewMessage] = useState("");
  const [isRecording, setIsRecording] = useState(false);

  const handleSendMessage = () => {
    if (newMessage.trim()) {
      // TODO: Send message logic
      setNewMessage("");
    }
  };

  const handleVoiceRecord = () => {
    setIsRecording(!isRecording);
    // TODO: Voice recording logic
  };

  if (selectedConversation) {
    const conversation = mockConversations.find(c => c.id === selectedConversation);
    if (!conversation) return null;

    return (
      <div className="min-h-screen bg-gray-50 flex flex-col">
        {/* Chat Header */}
        <div className="bg-white border-b border-gray-200 px-4 py-3">
          <div className="flex items-center gap-3">
            <Button
              size="icon"
              variant="ghost"
              onClick={() => setSelectedConversation(null)}
            >
              <ArrowLeft className="w-5 h-5" />
            </Button>
            <img
              src={conversation.participant.avatar}
              alt={conversation.participant.name}
              className="w-10 h-10 rounded-full object-cover"
            />
            <div className="flex-1">
              <div className="flex items-center gap-2">
                <h3 className="font-semibold">{conversation.participant.name}</h3>
                {conversation.participant.isOnline && (
                  <div className="w-2 h-2 bg-green-400 rounded-full"></div>
                )}
              </div>
              <p className="text-sm text-gray-500">{conversation.product.name}</p>
            </div>
            <div className="flex items-center gap-2">
              <Button size="icon" variant="ghost">
                <Phone className="w-5 h-5" />
              </Button>
              <Button size="icon" variant="ghost">
                <Video className="w-5 h-5" />
              </Button>
            </div>
          </div>
        </div>

        {/* Product Context */}
        <div className="bg-[#E67E22] bg-opacity-10 border-b border-[#E67E22] border-opacity-20 px-4 py-3">
          <div className="flex items-center gap-3">
            <img
              src={conversation.product.image}
              alt={conversation.product.name}
              className="w-12 h-12 rounded-lg object-cover"
            />
            <div>
              <h4 className="font-medium text-[#2C3E50]">{conversation.product.name}</h4>
              <p className="text-sm text-[#E67E22]">{conversation.product.price}</p>
            </div>
          </div>
        </div>

        {/* Messages */}
        <div className="flex-1 overflow-y-auto px-4 py-4 space-y-3">
          {mockMessages.map((message) => (
            <div
              key={message.id}
              className={`flex ${message.senderId === 'me' ? 'justify-end' : 'justify-start'}`}
            >
              <div
                className={`max-w-xs lg:max-w-md px-4 py-2 rounded-lg ${
                  message.senderId === 'me'
                    ? 'bg-[#E67E22] text-white'
                    : 'bg-white border border-gray-200'
                }`}
              >
                {message.isVoice ? (
                  <div className="flex items-center gap-2">
                    <Button size="icon" variant="ghost" className="w-8 h-8">
                      <svg className="w-4 h-4" fill="currentColor" viewBox="0 0 24 24">
                        <path d="M8 5v14l11-7z"/>
                      </svg>
                    </Button>
                    <span className="text-sm">{message.duration}</span>
                  </div>
                ) : (
                  <p className="text-sm">{message.content}</p>
                )}
                <p className={`text-xs mt-1 ${
                  message.senderId === 'me' ? 'text-white text-opacity-75' : 'text-gray-500'
                }`}>
                  {message.timestamp}
                </p>
              </div>
            </div>
          ))}
        </div>

        {/* Message Input */}
        <div className="bg-white border-t border-gray-200 px-4 py-3">
          <div className="flex items-center gap-2">
            <div className="flex-1 relative">
              <Input
                type="text"
                placeholder="Tapez votre message..."
                value={newMessage}
                onChange={(e) => setNewMessage(e.target.value)}
                onKeyPress={(e) => e.key === 'Enter' && handleSendMessage()}
                className="pr-12"
              />
            </div>
            <Button
              size="icon"
              variant="ghost"
              className={`${isRecording ? 'bg-red-500 text-white' : ''}`}
              onClick={handleVoiceRecord}
            >
              <Mic className="w-5 h-5" />
            </Button>
            <Button
              size="icon"
              className="bg-[#E67E22] hover:bg-[#D35400] text-white"
              onClick={handleSendMessage}
              disabled={!newMessage.trim()}
            >
              <Send className="w-5 h-5" />
            </Button>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Header */}
      <div className="bg-gradient-to-r from-[#E67E22] to-[#F39C12] px-4 py-6 text-white pattern-overlay">
        <div className="flex items-center gap-3">
          <div className="w-10 h-10 bg-white bg-opacity-20 rounded-xl flex items-center justify-center">
            <MessageCircle className="text-lg" />
          </div>
          <div>
            <h1 className="text-xl font-bold">Messages</h1>
            <p className="text-sm opacity-90">Négociez avec les vendeurs</p>
          </div>
        </div>
      </div>

      <div className="pb-20 px-4 py-4">
        <div className="space-y-3">
          {mockConversations.map((conversation) => (
            <Card
              key={conversation.id}
              className="cursor-pointer hover:shadow-md transition-shadow"
              onClick={() => setSelectedConversation(conversation.id)}
            >
              <CardContent className="p-4">
                <div className="flex items-center gap-3">
                  <div className="relative">
                    <img
                      src={conversation.participant.avatar}
                      alt={conversation.participant.name}
                      className="w-12 h-12 rounded-full object-cover"
                    />
                    {conversation.participant.isOnline && (
                      <div className="absolute -bottom-1 -right-1 w-4 h-4 bg-green-400 border-2 border-white rounded-full"></div>
                    )}
                  </div>

                  <div className="flex-1 min-w-0">
                    <div className="flex items-center justify-between mb-1">
                      <h3 className="font-semibold truncate">{conversation.participant.name}</h3>
                      <span className="text-xs text-gray-500 whitespace-nowrap">
                        {conversation.timestamp}
                      </span>
                    </div>

                    <div className="flex items-center gap-2 mb-2">
                      <img
                        src={conversation.product.image}
                        alt={conversation.product.name}
                        className="w-6 h-6 rounded object-cover"
                      />
                      <span className="text-sm text-[#E67E22] font-medium">
                        {conversation.product.name}
                      </span>
                    </div>

                    <div className="flex items-center justify-between">
                      <p className="text-sm text-gray-600 truncate flex-1 mr-2">
                        {conversation.lastMessage}
                      </p>
                      {conversation.unread > 0 && (
                        <Badge className="bg-[#E67E22] text-white">
                          {conversation.unread}
                        </Badge>
                      )}
                    </div>
                  </div>
                </div>
              </CardContent>
            </Card>
          ))}
        </div>

        {mockConversations.length === 0 && (
          <div className="text-center py-12">
            <div className="w-16 h-16 bg-gray-100 rounded-full flex items-center justify-center mx-auto mb-4">
              <MessageCircle className="w-8 h-8 text-gray-400" />
            </div>
            <h3 className="text-lg font-semibold text-gray-900 mb-2">
