interface ProductCardProps {
  product: ProductWithDetails;
  onBarter?: (product: ProductWithDetails) => void;
  onVoiceChat?: (product: ProductWithDetails) => void;
  onShare?: (product: ProductWithDetails) => void;
}

export function ProductCard({ product, onBarter, onVoiceChat, onShare }: ProductCardProps) {
  const [isLiked, setIsLiked] = useState(false);

  const getPriceIndicator = (price: string) => {
    const priceNum = parseFloat(price);
    if (priceNum < 2000) {
      return { 
        label: "Bon prix", 
        color: "bg-[#27AE60] text-white",
        pulse: true 
      };
    } else if (priceNum < 5000) {
      return { 
        label: "Prix moyen", 
        color: "bg-[#F39C12] text-white",
        pulse: false 
      };
    } else {
      return { 
        label: "Prix élevé", 
        color: "bg-[#E74C3C] text-white",
        pulse: false 
      };
    }
  };

  const priceIndicator = getPriceIndicator(product.price);
  const timeAgo = "il y a 15 min"; // Mock time for now

  return (
    <Card className="mb-4 overflow-hidden shadow-sm border border-gray-100">
      <div className="relative">
        <img
          src={product.imageUrl || "/api/placeholder/400/200"}
          alt={product.name}
          className="w-full h-40 object-cover"
        />
        <div className="absolute top-3 left-3">
          <Badge 
            className={cn(
              priceIndicator.color,
              priceIndicator.pulse && "animate-pulse"
            )}
          >
            {priceIndicator.label}
          </Badge>
        </div>
        <Button
          size="icon"
          variant="ghost"
          className="absolute top-3 right-3 w-8 h-8 bg-white bg-opacity-90 rounded-full hover:bg-opacity-100"
          onClick={() => setIsLiked(!isLiked)}
        >
          <Heart 
            className={cn(
              "w-4 h-4",
              isLiked ? "fill-red-500 text-red-500" : "text-gray-600"
            )}
          />
        </Button>
      </div>

      <CardContent className="p-4">
        <div className="flex items-start justify-between mb-2">
          <div>
            <h3 className="font-semibold text-[#2C3E50]">{product.name}</h3>
            <p className="text-sm text-gray-500">
              {product.vendor?.fullName || product.vendor?.username}
            </p>
          </div>
          <div className="text-right">
            <p className="text-lg font-bold text-[#2C3E50]">
              {parseInt(product.price).toLocaleString()} CFA/{product.unit}
            </p>
            {/* Mock price change */}
            <p className="text-xs text-[#27AE60]">↓ 200 CFA</p>
          </div>
        </div>

        <div className="flex items-center gap-4 mb-3 text-xs text-gray-500">
          {product.distance && (
            <div className="flex items-center">
              <MapPin className="w-3 h-3 mr-1" />
              <span>{product.distance.toFixed(1)} km</span>
            </div>
          )}
          <div className="flex items-center">
            <Clock className="w-3 h-3 mr-1" />
            <span>{timeAgo}</span>
          </div>
          {product.rating && parseFloat(product.rating) > 0 && (
            <div className="flex items-center">
              <Star className="w-3 h-3 mr-1 fill-[#F39C12] text-[#F39C12]" />
              <span>{product.rating} ({product.reviewCount})</span>
