import { useState, useEffect } from 'react';
import { useToast } from '@/hooks/use-toast';

interface SimpleUser {
  username: string;
  fullName: string;
  isVendor: boolean;
}

export function useSimpleAuth() {
  const [user, setUser] = useState<SimpleUser | null>(null);
  const [loading, setLoading] = useState(true);
  const { toast } = useToast();

  useEffect(() => {
    const savedUser = localStorage.getItem('wagaraaga_user');
    if (savedUser) {
      setUser(JSON.parse(savedUser));
    }
    setLoading(false);
  }, []);

  const login = (userData: SimpleUser) => {
    setUser(userData);
    localStorage.setItem('wagaraaga_user', JSON.stringify(userData));
    toast({
      title: "Connexion réussie",
      description: `Bienvenue ${userData.fullName}!`,
    });
  };

  const logout = () => {
    setUser(null);
    localStorage.removeItem('wagaraaga_user');
    toast({
      title: "Déconnexion",
      description: "À bientôt!",
    });
  };

  const register = (userData: SimpleUser) => {
    login(userData);
  };

  return {
    user,
    loading,
    login,
    logout,
    register,
    isAuthenticated: !!user
  };
}

