import { useState } from 'react';

interface Address {
  display_name: string;
  lat: string;
  lon: string;
  place_id: string;
}

export function useFreeGeocoding() {
  const [loading, setLoading] = useState(false);

  const searchAddresses = async (query: string): Promise<Address[]> => {
    if (!query.trim()) return [];

    setLoading(true);
    try {
      // Utiliser Nominatim (OpenStreetMap) - gratuit et illimité
      const response = await fetch(
        `https://nominatim.openstreetmap.org/search?format=json&q=${encodeURIComponent(query)}&countrycodes=bf&limit=5`
      );

      if (!response.ok) throw new Error('Erreur de recherche');

      const data = await response.json();
      return data;
    } catch (error) {
      console.error('Erreur géocodage:', error);
      return [];
    } finally {
      setLoading(false);
    }
  };

  const reverseGeocode = async (lat: number, lon: number): Promise<string> => {
    try {
      const response = await fetch(
        `https://nominatim.openstreetmap.org/reverse?format=json&lat=${lat}&lon=${lon}`
      );

      if (!response.ok) throw new Error('Erreur géocodage inverse');

      const data = await response.json();
      return data.display_name || 'Adresse inconnue';
    } catch (error) {
      console.error('Erreur géocodage inverse:', error);
      return 'Adresse inconnue';
    }
  };

  return {
    searchAddresses,
    reverseGeocode,
    loading
  };
}

