import { useEffect, useState } from "react";

interface MapsLoaderProps {
  children: React.ReactNode;
  fallback?: React.ReactNode;
}

declare global {
  interface Window {
    google: typeof google;
    initGoogleMaps: () => void;
  }
}

export function MapsLoader({ children, fallback }: MapsLoaderProps) {
  const [isLoaded, setIsLoaded] = useState(false);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    // Vérifier si Google Maps est déjà chargé
    if (window.google && window.google.maps) {
      setIsLoaded(true);
      return;
    }

    // Récupérer la clé API depuis les variables d'environnement
    const apiKey = import.meta.env.VITE_GOOGLE_MAPS_API_KEY;

    if (!apiKey) {
      setError("Clé API Google Maps manquante. Ajoutez VITE_GOOGLE_MAPS_API_KEY dans vos secrets.");
      return;
    }

    // Fonction de callback globale
    window.initGoogleMaps = () => {
      setIsLoaded(true);
    };

    // Charger le script Google Maps Platform API
    const script = document.createElement("script");
    script.src = `https://maps.googleapis.com/maps/api/js?key=${apiKey}&callback=initGoogleMaps&libraries=places,geometry,marker&language=fr&region=BF&v=weekly`;
    script.async = true;
    script.defer = true;

    script.onerror = () => {
      setError("Impossible de charger Google Maps API");
    };

    document.head.appendChild(script);

    return () => {
      // Nettoyer le script si le composant est démonté
      if (script.parentNode) {
        script.parentNode.removeChild(script);
      }
      delete window.initGoogleMaps;
    };
  }, []);

  if (error) {
    return (
      <div className="p-4 bg-red-50 border border-red-200 rounded-lg">
        <p className="text-red-600 text-sm font-medium mb-2">Erreur Google Maps</p>
        <p className="text-red-500 text-xs">{error}</p>
      </div>
    );
  }

  if (!isLoaded) {
    return fallback || (
      <div className="flex items-center justify-center p-8">
        <div className="text-center">
          <div className="w-8 h-8 border-2 border-[#E67E22] border-t-transparent rounded-full animate-spin mx-auto mb-2"></div>
          <p className="text-sm text-gray-600">Chargement de Google Maps...</p>
        </div>
      </div>
    );
  }

  return <>{children}</>;
}

