import { Home, Search, ArrowRightLeft, MessageCircle, User } from "lucide-react";
import { Link, useLocation } from "wouter";
import { cn } from "@/lib/utils";

export function BottomNavigation() {
  const [location] = useLocation();

  const navItems = [
    { icon: Home, label: "Accueil", path: "/" },
    { icon: Search, label: "Marché", path: "/marketplace" },
    { icon: ArrowRightLeft, label: "Troquer", path: "/bartering", badge: 2 },
    { icon: MessageCircle, label: "Messages", path: "/messages" },
    { icon: User, label: "Profil", path: "/profile" },
  ];

  return (
    <div className="fixed bottom-0 left-1/2 transform -translate-x-1/2 w-full max-w-sm bg-white border-t border-gray-200 px-4 py-2 z-50">
      <div className="flex items-center justify-around">
        {navItems.map((item) => {
          const isActive = location === item.path;
          const Icon = item.icon;

          return (
            <Link key={item.path} href={item.path}>
              <div className={cn(
                "flex flex-col items-center gap-1 py-2 px-3 rounded-lg transition-colors relative",
                isActive 
                  ? "text-[#E67E22]" 
                  : "text-gray-400 hover:text-gray-600"
              )}>
                <Icon className="w-5 h-5" />
                <span className="text-xs font-medium">{item.label}</span>
                {item.badge && (
                  <span className="absolute -top-1 -right-1 w-4 h-4 bg-[#27AE60] text-white text-xs rounded-full flex items-center justify-center">
                    {item.badge}
                  </span>
                )}
              </div>
            </Link>
          );
        })}
      </div>
    </div>
  );
}

