  --sidebar-accent-foreground: hsl(22, 79%, 52%);
  --sidebar-border: hsl(215, 27%, 16%);
  --sidebar-ring: hsl(22, 79%, 52%);
}

@layer base {
  * {
    @apply border-border;
  }

  body {
    @apply font-sans antialiased bg-background text-foreground;
  }
}

/* Custom WagaRaaga styles */
.gradient-bg {
  background: linear-gradient(135deg, #E67E22 0%, #F39C12 100%);
}

.pattern-overlay {
  background-image: 
    radial-gradient(circle at 25% 25%, #ffffff10 2px, transparent 2px),
    radial-gradient(circle at 75% 75%, #ffffff10 2px, transparent 2px);
  background-size: 24px 24px;
}

.price-pulse {
  animation: price-pulse 2s cubic-bezier(0.4, 0, 0.6, 1) infinite;
}

@keyframes price-pulse {
  0%, 100% { 
    opacity: 1; 
    transform: scale(1);
  }
  50% { 
    opacity: 0.8; 
    transform: scale(1.02);
  }
}

/* African-inspired accent patterns */
.african-pattern {
  background-image: 
    repeating-linear-gradient(
      45deg,
      transparent,
      transparent 2px,
      rgba(230, 126, 34, 0.1) 2px,
      rgba(230, 126, 34, 0.1) 4px
    );
}

/* Mobile-first responsive utilities */
.mobile-container {
  max-width: 428px;
  margin: 0 auto;
  min-height: 100vh;
  background: white;
  box-shadow: 0 0 30px rgba(0, 0, 0, 0.1);
}

/* Voice recording animation */
.recording-pulse {
  animation: recording-pulse 1.5s ease-in-out infinite;
}

@keyframes recording-pulse {
  0% {
    box-shadow: 0 0 0 0 rgba(239, 68, 68, 0.7);
  }
  70% {
    box-shadow: 0 0 0 10px rgba(239, 68, 68, 0);
  }
  100% {
    box-shadow: 0 0 0 0 rgba(239, 68, 68, 0);
  }
}

/* Price indicator colors */
.price-good {
  background-color: var(--good-deal);
  color: white;
}

.price-average {
  background-color: var(--amber);
  color: white;
}

.price-high {
  background-color: var(--high-price);
  color: white;
}

/* Smooth transitions for better UX */
.transition-smooth {
  transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
}

/* Custom scrollbar for mobile */
.custom-scrollbar::-webkit-scrollbar {
  width: 4px;
}

.custom-scrollbar::-webkit-scrollbar-track {
  background: transparent;
}

.custom-scrollbar::-webkit-scrollbar-thumb {
  background: rgba(0, 0, 0, 0.1);
  border-radius: 2px;
}

.custom-scrollbar::-webkit-scrollbar-thumb:hover {
  background: rgba(0, 0, 0, 0.2);
}

/* Focus states for accessibility */
.focus-ring {
  @apply focus:outline-none focus:ring-2 focus:ring-primary focus:ring-offset-2;
}

/* Safe area padding for mobile devices */
.safe-area-top {
  padding-top: env(safe-area-inset-top);
}

.safe-area-bottom {
  padding-bottom: env(safe-area-inset-bottom);
}

