import { useState } from "react";
import { Bell, Mic, Store } from "lucide-react";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { VoiceModal } from "@/components/voice/voice-modal";
import { useToast } from "@/hooks/use-toast";

interface HeaderProps {
  onSearch?: (query: string) => void;
}

export function Header({ onSearch }: HeaderProps) {
  const [searchQuery, setSearchQuery] = useState("");
  const [showVoiceModal, setShowVoiceModal] = useState(false);
  const { toast } = useToast();

  const handleSearch = (e: React.FormEvent) => {
    e.preventDefault();
    if (searchQuery.trim()) {
      onSearch?.(searchQuery.trim());
    }
  };

  const handleVoiceTranscript = (transcript: string) => {
    setSearchQuery(transcript);
    setShowVoiceModal(false);
    if (transcript.trim()) {
      onSearch?.(transcript.trim());
    }
  };

  const handleNotificationClick = () => {
    toast({
      title: "Notifications",
      description: "3 nouvelles notifications disponibles",
    });
  };

  return (
    <>
      {/* Status Bar */}
      <div className="bg-gradient-to-r from-[#E67E22] to-[#F39C12] px-4 py-2 text-white text-sm flex justify-between items-center pattern-overlay">
        <div className="flex items-center gap-1">
          <div className="w-1 h-1 bg-white rounded-full"></div>
          <div className="w-1 h-1 bg-white rounded-full"></div>
          <div className="w-1 h-1 bg-white rounded-full"></div>
          <span className="text-xs ml-1">Orange</span>
        </div>
        <div className="text-xs font-medium">
          {new Date().toLocaleTimeString('fr-FR', { 
            hour: '2-digit', 
            minute: '2-digit' 
          })}
        </div>
        <div className="flex items-center gap-1">
          <span className="text-xs">85%</span>
          <div className="w-4 h-2 border border-white rounded-sm">
            <div className="w-3 h-1 bg-white rounded-sm m-0.5"></div>
          </div>
        </div>
      </div>

      {/* App Header */}
      <div className="bg-gradient-to-r from-[#E67E22] to-[#F39C12] px-4 py-6 text-white pattern-overlay">
        <div className="flex items-center justify-between mb-4">
          <div className="flex items-center gap-3">
            <div className="w-12 h-12 bg-white rounded-xl flex items-center justify-center shadow-lg">
              <Store className="text-[#E67E22] text-xl" />
            </div>
            <div>
              <h1 className="text-2xl font-bold tracking-wide">WagaRaaga</h1>
              <p className="text-sm opacity-90">Marché Sandaga, Dakar</p>
            </div>
          </div>
          <div className="flex items-center gap-3">
            <Button
              size="icon"
              variant="ghost"
              className="w-10 h-10 bg-white bg-opacity-20 rounded-xl hover:bg-white hover:bg-opacity-30"
              onClick={() => setShowVoiceModal(true)}
            >
              <Mic className="text-lg" />
            </Button>
            <Button
              size="icon"
              variant="ghost"
              className="w-10 h-10 bg-white bg-opacity-20 rounded-xl hover:bg-white hover:bg-opacity-30 relative"
              onClick={handleNotificationClick}
            >
              <Bell className="text-lg" />
              <span className="absolute -top-1 -right-1 w-4 h-4 bg-[#E74C3C] text-white text-xs rounded-full flex items-center justify-center">
                3
              </span>
            </Button>
          </div>
        </div>

        {/* Search Bar */}
        <form onSubmit={handleSearch} className="relative">
          <Input
            type="text"
            placeholder="Rechercher manioc, poisson, tissus..."
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
            className="w-full bg-white bg-opacity-20 backdrop-blur-sm text-white placeholder-white placeholder-opacity-75 
                       border-white border-opacity-30 focus:ring-2 focus:ring-white focus:ring-opacity-50 focus:border-transparent"
          />
          <Button
            type="submit"
            size="icon"
            variant="ghost"
            className="absolute right-1 top-1/2 transform -translate-y-1/2 hover:bg-transparent"
          >
            <svg className="w-4 h-4 text-white opacity-75" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M21 21l-6-6m2-5a7 7 0 11-14 0 7 7 0 0114 0z" />
            </svg>
          </Button>
        </form>
      </div>

      <VoiceModal
        open={showVoiceModal}
        onOpenChange={setShowVoiceModal}
        onTranscript={handleVoiceTranscript}
      />
    </>
  );
}

