import React, { useState, useEffect } from 'react';
import L from 'leaflet';
import 'leaflet/dist/leaflet.css';

const LeafletMap = () => {
    const [map, setMap] = useState(null);
    const [marker, setMarker] = useState(null);
    const [location, setLocation] = useState({ lat: 48.8566, lng: 2.3522 }); // Default to Paris

    useEffect(() => {
        const initialMap = L.map('map').setView([location.lat, location.lng], 13);
        L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png', {
            maxZoom: 19,
        }).addTo(initialMap);

        const newMarker = L.marker([location.lat, location.lng]).addTo(initialMap);
        setMap(initialMap);
        setMarker(newMarker);

        return () => {
            initialMap.remove();
        };
    }, []);

    const handleSearch = async (event) => {
        const query = event.target.value;
        if (query.length < 3) return; // Minimal length for search

        const response = await fetch(`https://nominatim.openstreetmap.org/search?format=json&q=${query}`);
        const results = await response.json();

        if (results.length > 0) {
            const { lat, lon } = results[0];
            setLocation({ lat, lng: lon });
            if (marker) {
                marker.setLatLng([lat, lon]);
            }
            if (map) {
                map.setView([lat, lon], 13);
            }
        }
    };

    return (
        <div>
            <input
                type="text"
                placeholder="Rechercher un lieu..."
                onChange={handleSearch}
                style={{ width: '300px', height: '40px', padding: '5px', marginBottom: '10px' }}
            />
            <div id="map" style={{ height: '400px' }} />
        </div>
    );
};

export default LeafletMap;

