import { useState } from "react";
import { useQuery } from "@tanstack/react-query";
import { Header } from "@/components/layout/header";
import { ProductCard } from "@/components/product/product-card";
import { CategoryGrid } from "@/components/product/category-grid";
import { MapsLoader } from "@/components/maps/maps-loader";
import { GoogleMap } from "@/components/maps/google-map";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Skeleton } from "@/components/ui/skeleton";
import { Badge } from "@/components/ui/badge";
import { Search, Filter, Plus, Grid, List, Map } from "lucide-react";
import { useGeolocation } from "@/hooks/use-geolocation";
import { useToast } from "@/hooks/use-toast";
import { useLocation } from "wouter";
import type { ProductWithDetails } from "@shared/schema";

export default function MarketplacePage() {
  const [, setLocation] = useLocation();
  const [searchQuery, setSearchQuery] = useState("");
  const [selectedCategory, setSelectedCategory] = useState<string>("");
  const [viewMode, setViewMode] = useState<"grid" | "list" | "map">("grid");
  const [showFilters, setShowFilters] = useState(false);
  const { latitude, longitude, error: locationError } = useGeolocation();
  const { toast } = useToast();

  const { data: searchResults, isLoading: searchLoading } = useQuery({
    queryKey: ["/api/search", searchQuery, latitude, longitude],
    enabled: searchQuery.length > 0 && latitude !== null && longitude !== null,
  });

  const { data: allProducts, isLoading: productsLoading } = useQuery({
    queryKey: ["/api/products", selectedCategory, latitude, longitude],
    enabled: latitude !== null && longitude !== null,
  });

  const displayProducts = searchQuery ? searchResults : allProducts;
  const isLoading = searchQuery ? searchLoading : productsLoading;

  const handleSearch = (query: string) => {
    setSearchQuery(query);
  };

  const handleVoiceChat = (product: ProductWithDetails) => {
    toast({
      title: "Chat vocal",
      description: `Démarrage du chat vocal avec ${product.vendor?.fullName}`,
    });
  };

  const handleBarter = (product: ProductWithDetails) => {
    setLocation("/bartering");
  };

  if (locationError) {
    return (
      <div className="min-h-screen bg-gray-50 pb-20">
        <Header title="Marketplace" />
        <div className="p-4 text-center">
          <p className="text-red-500">Erreur de géolocalisation : {locationError}</p>
          <Button 
            onClick={() => window.location.reload()} 
            className="mt-4 bg-[#E67E22] hover:bg-[#D35400]"
          >
            Réessayer
          </Button>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50 pb-20">
      <Header title="Marketplace" />

      <div className="p-4 space-y-4">
        {/* Search and Filters */}
        <div className="space-y-3">
          <div className="flex gap-2">
            <div className="relative flex-1">
              <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 w-4 h-4 text-gray-400" />
              <Input
                placeholder="Rechercher des produits..."
                value={searchQuery}
                onChange={(e) => handleSearch(e.target.value)}
                className="pl-10"
              />
            </div>
            <Button
              variant="outline"
              size="icon"
              onClick={() => setShowFilters(!showFilters)}
              className="border-gray-300"
            >
              <Filter className="w-4 h-4" />
            </Button>
            <div className="flex border border-gray-300 rounded-md">
              <Button
                variant={viewMode === "grid" ? "default" : "ghost"}
                size="sm"
                onClick={() => setViewMode("grid")}
                className="rounded-r-none"
              >
                <Grid className="w-4 h-4" />
              </Button>
              <Button
                variant={viewMode === "list" ? "default" : "ghost"}
                size="sm"
                onClick={() => setViewMode("list")}
                className="rounded-none border-x"
              >
                <List className="w-4 h-4" />
              </Button>
              <Button
                variant={viewMode === "map" ? "default" : "ghost"}
                size="sm"
                onClick={() => setViewMode("map")}
                className="rounded-l-none"
              >
                <Map className="w-4 h-4" />
              </Button>
            </div>
          </div>

          {/* Add Product Button */}
          <Button
            onClick={() => setLocation("/sell")}
            className="w-full bg-[#E67E22] hover:bg-[#D35400] text-white font-medium"
          >
            <Plus className="w-4 h-4 mr-2" />
            Vendre un produit
          </Button>
        </div>

        {/* Category Filter */}
        {showFilters && (
          <div className="bg-white rounded-lg p-4 border border-gray-200">
            <h3 className="font-medium text-[#2C3E50] mb-3">Filtrer par catégorie</h3>
            <CategoryGrid
              selectedCategory={selectedCategory}
              onCategorySelect={setSelectedCategory}
            />
          </div>
        )}

        {/* Search Results Info */}
        {searchQuery && (
          <div className="flex items-center justify-between">
            <div className="flex items-center gap-2">
              <Badge variant="secondary" className="bg-[#E67E22] text-white">
                {displayProducts?.length || 0} résultats
              </Badge>
              <span className="text-sm text-gray-600">pour "{searchQuery}"</span>
            </div>
            {searchQuery && (
              <Button
                variant="ghost"
                size="sm"
                onClick={() => setSearchQuery("")}
                className="text-[#E67E22]"
              >
                Effacer
              </Button>
            )}
          </div>
        )}

        {/* Products Grid/List/Map */}
        <div className="space-y-4">
          {viewMode === "map" ? (
            <div className="space-y-4">
              {isLoading ? (
                <Skeleton className="h-96 rounded-lg" />
              ) : displayProducts && displayProducts.length > 0 ? (
                <MapsLoader>
                  <GoogleMap
                    center={latitude && longitude ? { lat: latitude, lng: longitude } : { lat: 12.3714, lng: -1.5197 }}
                    zoom={12}
                    markers={displayProducts
                      .filter(product => product.latitude && product.longitude)
                      .map(product => ({
                        id: product.id,
                        position: { lat: product.latitude!, lng: product.longitude! },
                        title: product.name,
                        info: `${parseInt(product.price).toLocaleString()} CFA/${product.unit} - ${product.vendor?.fullName || 'Vendeur'}`
                      }))}
                    height="400px"
                    className="border border-gray-200 rounded-lg"
                  />
                  <div className="bg-green-50 border border-green-200 rounded-lg p-3 mt-2">
                    <p className="text-sm text-green-700">
                      🗺️ {displayProducts.filter(p => p.latitude && p.longitude).length} produits affichés sur la carte du Burkina Faso
                    </p>
                  </div>
                </MapsLoader>
              ) : (
                <div className="text-center py-12">
                  <Map className="w-16 h-16 text-gray-400 mx-auto mb-4" />
                  <h3 className="text-lg font-medium text-gray-900 mb-2">Aucun produit à afficher</h3>
                  <p className="text-gray-500">Aucun produit avec localisation disponible</p>
                </div>
              )}
            </div>
          ) : (
            <>
              {isLoading ? (
                <div className={viewMode === "grid" ? "grid grid-cols-1 gap-4" : "space-y-4"}>
                  {Array.from({ length: 6 }).map((_, i) => (
                    <Skeleton key={i} className="h-64 rounded-lg" />
                  ))}
                </div>
              ) : displayProducts && displayProducts.length > 0 ? (
                <div className={viewMode === "grid" ? "grid grid-cols-1 gap-4" : "space-y-4"}>
                  {displayProducts.map((product) => (
                    <ProductCard
                      key={product.id}
                      product={product}
                      onVoiceChat={handleVoiceChat}
                      onBarter={handleBarter}
                    />
                  ))}
                </div>
              ) : (
                <div className="text-center py-12">
                  <div className="w-16 h-16 bg-gray-100 rounded-full flex items-center justify-center mx-auto mb-4">
                    <Search className="w-8 h-8 text-gray-400" />
                  </div>
                  <h3 className="text-lg font-medium text-gray-900 mb-2">
                    {searchQuery ? "Aucun résultat trouvé" : "Aucun produit disponible"}
                  </h3>
                  <p className="text-gray-500 mb-4">
                    {searchQuery 
                      ? "Essayez avec d'autres mots-clés" 
                      : "Soyez le premier à publier un produit!"}
                  </p>
                  {!searchQuery && (
                    <Button
                      onClick={() => setLocation("/sell")}
                      className="bg-[#E67E22] hover:bg-[#D35400] text-white"
                    >
                      <Plus className="w-4 h-4 mr-2" />
                      Publier un produit
                    </Button>
                  )}
                </div>
              )}
            </>
          )}
        </div>
      </div>
    </div>
  );
}

