import { useQuery } from "@tanstack/react-query";
import { Card } from "@/components/ui/card";
import { Skeleton } from "@/components/ui/skeleton";
import type { Category } from "@shared/schema";

interface CategoryGridProps {
  onCategorySelect?: (categoryId: string) => void;
}

export function CategoryGrid({ onCategorySelect }: CategoryGridProps) {
  const { data: categories, isLoading } = useQuery({
    queryKey: ["/api/categories"],
  });

  if (isLoading) {
    return (
      <div className="px-4 py-2">
        <h2 className="text-lg font-semibold mb-4">Catégories Populaires</h2>
        <div className="grid grid-cols-4 gap-3">
          {Array.from({ length: 4 }).map((_, i) => (
            <div key={i} className="text-center">
              <Skeleton className="w-16 h-16 rounded-2xl mx-auto mb-2" />
              <Skeleton className="h-3 w-12 mx-auto" />
            </div>
          ))}
        </div>
      </div>
    );
  }

  const getCategoryGradient = (index: number) => {
    const gradients = [
      "from-green-400 to-green-600",
      "from-blue-400 to-blue-600", 
      "from-purple-400 to-purple-600",
      "from-orange-400 to-orange-600",
    ];
    return gradients[index % gradients.length];
  };

  const getCategoryIcon = (name: string) => {
    const icons: Record<string, string> = {
      'Légumes': '🌱',
      'Poissons': '🐟',
      'Tissus': '✂️',
      'Fruits': '🍎',
      'Vêtements': '👗',
      'Chaussures': '👠',
      'Engins deux roues': '🏍️',
      'Véhicules': '🚗',
      'Appareils Divers': '📱',
    };
    return icons[name] || '📦';
  };

  return (
    <div className="px-4 py-2">
      <h2 className="text-lg font-semibold mb-4">Catégories Populaires</h2>
      <div className="grid grid-cols-4 gap-3 mb-6">
        {categories?.map((category: Category, index: number) => (
          <Card
            key={category.id}
            className="text-center cursor-pointer hover:shadow-md transition-shadow p-0 border-0"
            onClick={() => onCategorySelect?.(category.id)}
          >
            <div className={`w-16 h-16 bg-gradient-to-br ${getCategoryGradient(index)} rounded-2xl flex items-center justify-center mb-2 mx-auto shadow-sm`}>
              <span className="text-white text-2xl">
                {getCategoryIcon(category.name)}
              </span>
            </div>
            <span className="text-xs text-gray-600 block pb-2">{category.name}</span>
          </Card>
        ))}
      </div>
    </div>
  );
}

