import { useState, useEffect } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Switch } from "@/components/ui/switch";
import { Badge } from "@/components/ui/badge";
import { Separator } from "@/components/ui/separator";
import { 
  User, 
  MapPin, 
  Phone, 
  Settings, 
  Bell, 
  Shield, 
  HelpCircle, 
  LogOut,
  Edit3,
  Camera,
  Star,
  Package,
  TrendingUp,
  Award
} from "lucide-react";
import { useToast } from "@/hooks/use-toast";

export default function ProfilePage() {
  const [isEditing, setIsEditing] = useState(false);
  const [notificationsEnabled, setNotificationsEnabled] = useState(true);
  const [voiceEnabled, setVoiceEnabled] = useState(true);
  const [lowDataMode, setLowDataMode] = useState(false);
  const { toast } = useToast();
  const queryClient = useQueryClient();

  // Fetch user profile
  const { data: userProfile, isLoading: profileLoading } = useQuery({
    queryKey: ["/api/auth/me"],
  });

  // Profile update mutation
  const updateProfileMutation = useMutation({
    mutationFn: async (updates: any) => {
      const response = await fetch("/api/auth/profile", {
        method: "PATCH",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(updates),
      });
      if (!response.ok) {
        throw new Error("Erreur de mise à jour du profil");
      }
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/auth/me"] });
      setIsEditing(false);
      toast({
        title: "Profil mis à jour",
        description: "Vos informations ont été sauvegardées avec succès.",
      });
    },
    onError: () => {
      toast({
        title: "Erreur",
        description: "Impossible de mettre à jour le profil.",
        variant: "destructive",
      });
    },
  });

  // Local state for editing
  const [editData, setEditData] = useState({
    fullName: "",
    phone: "",
    location: "",
  });

  // Initialize edit data when user profile loads
  useEffect(() => {
    if (userProfile) {
      setEditData({
        fullName: userProfile.fullName || "",
        phone: userProfile.phone || "",
        location: userProfile.location || "",
      });
    }
  }, [userProfile]);

  // Stats based on user profile data with fallbacks
  const stats = [
    { icon: Star, label: "Note moyenne", value: userProfile?.rating?.toString() || "4.8", color: "text-amber-600" },
    { icon: Package, label: "Ventes", value: userProfile?.completedDeals?.toString() || "47", color: "text-blue-600" },
    { icon: TrendingUp, label: "Économies", value: userProfile?.savings ? `${userProfile.savings.toLocaleString()} CFA` : "25,600 CFA", color: "text-green-600" },
    { icon: Award, label: "Niveau", value: "Vendeur Pro", color: "text-purple-600" },
  ];

  const handleSaveProfile = () => {
    updateProfileMutation.mutate(editData);
  };

  const handleLogout = () => {
    // Clear user session/token here
    localStorage.removeItem("user-token");
    queryClient.clear();
    toast({
      title: "Déconnexion",
      description: "Vous avez été déconnecté avec succès.",
    });
    // Redirect to auth page
    window.location.href = "/auth";
  };

  if (profileLoading) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="text-center">
          <div className="w-8 h-8 border-4 border-[#E67E22] border-t-transparent rounded-full animate-spin mx-auto mb-4"></div>
          <p className="text-gray-600">Chargement du profil...</p>
        </div>
      </div>
    );
  }

  if (!userProfile) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="text-center">
          <p className="text-gray-600 mb-4">Vous devez être connecté pour accéder à cette page.</p>
          <Button 
            onClick={() => window.location.href = "/auth"}
            className="bg-[#E67E22] hover:bg-[#D35400] text-white"
          >
            Se connecter
          </Button>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Header */}
      <div className="bg-gradient-to-r from-[#E67E22] to-[#F39C12] px-4 py-6 text-white pattern-overlay">
        <div className="flex items-center justify-between mb-4">
          <div>
            <h1 className="text-xl font-bold">Mon Profil</h1>
            <p className="text-sm opacity-90">Gérez votre compte WagaRaaga</p>
          </div>
          <Button
            size="icon"
            variant="ghost"
            className="w-10 h-10 bg-white bg-opacity-20 rounded-xl hover:bg-white hover:bg-opacity-30"
            onClick={() => setIsEditing(!isEditing)}
          >
            <Edit3 className="w-5 h-5" />
          </Button>
        </div>
      </div>

      <div className="pb-20 px-4 py-6 space-y-6">
        {/* Profile Card */}
        <Card>
          <CardContent className="p-6">
            <div className="flex items-center gap-4 mb-4">
              <div className="relative">
                <img
                  src={userProfile.avatar}
                  alt={userProfile.name}
                  className="w-20 h-20 rounded-full object-cover"
                />
                {isEditing && (
                  <Button
                    size="icon"
                    className="absolute -bottom-2 -right-2 w-8 h-8 bg-[#E67E22] hover:bg-[#D35400] text-white rounded-full"
                  >
                    <Camera className="w-4 h-4" />
                  </Button>
                )}
              </div>

              <div className="flex-1">
                {isEditing ? (
                  <div className="space-y-2">
                    <div>
                      <Label htmlFor="name">Nom complet</Label>
                      <Input
                        id="name"
                        value={editData.fullName}
                        onChange={(e) => setEditData(prev => ({ ...prev, fullName: e.target.value }))}
                      />
                    </div>
                  </div>
                ) : (
                  <div>
                    <h2 className="text-xl font-bold text-[#2C3E50]">{userProfile.fullName}</h2>
                    <p className="text-gray-600">@{userProfile.username}</p>
                    {userProfile.isVendor && (
                      <Badge className="mt-1 bg-[#E67E22] text-white">
                        Vendeur Vérifié
                      </Badge>
                    )}
                  </div>
                )}
              </div>
            </div>

            {/* Contact Info */}
            <div className="space-y-3">
              <div className="flex items-center gap-3">
                <Phone className="w-5 h-5 text-gray-400" />
                {isEditing ? (
                  <Input
                    value={editData.phone}
                    onChange={(e) => setEditData(prev => ({ ...prev, phone: e.target.value }))}
                    className="flex-1"
                  />
                ) : (
                  <span className="text-gray-700">{userProfile.phone || "Non renseigné"}</span>
                )}
              </div>

              <div className="flex items-center gap-3">
                <MapPin className="w-5 h-5 text-gray-400" />
                {isEditing ? (
                  <Input
                    value={editData.location}
                    onChange={(e) => setEditData(prev => ({ ...prev, location: e.target.value }))}
                    className="flex-1"
                  />
                ) : (
                  <span className="text-gray-700">{userProfile.location || "Non renseignée"}</span>
                )}
              </div>
            </div>

            {isEditing && (
              <div className="flex gap-2 mt-4">
                <Button 
                  onClick={handleSaveProfile}
                  className="flex-1 bg-[#E67E22] hover:bg-[#D35400] text-white"
                >
                  Sauvegarder
                </Button>
                <Button 
                  variant="outline" 
                  onClick={() => setIsEditing(false)}
                  className="flex-1"
                >
                  Annuler
                </Button>
              </div>
            )}
          </CardContent>
        </Card>

        {/* Stats Grid */}
        <div className="grid grid-cols-2 gap-3">
          {stats.map((stat, index) => {
            const Icon = stat.icon;
            return (
              <Card key={index}>
                <CardContent className="p-4 text-center">
                  <Icon className={`w-8 h-8 mx-auto mb-2 ${stat.color}`} />
                  <p className="text-2xl font-bold text-[#2C3E50]">{stat.value}</p>
                  <p className="text-sm text-gray-600">{stat.label}</p>
                </CardContent>
              </Card>
            );
          })}
        </div>

        {/* Settings */}
        <Card>
          <CardHeader>
            <CardTitle className="flex items-center gap-2">
              <Settings className="w-5 h-5" />
              Paramètres
            </CardTitle>
          </CardHeader>
          <CardContent className="space-y-4">
            <div className="flex items-center justify-between">
              <div className="flex items-center gap-3">
                <Bell className="w-5 h-5 text-gray-400" />
                <div>
                  <p className="font-medium">Notifications</p>
                  <p className="text-sm text-gray-600">Recevoir les alertes de prix</p>
                </div>
              </div>
              <Switch
                checked={notificationsEnabled}
                onCheckedChange={setNotificationsEnabled}
              />
            </div>

            <Separator />

            <div className="flex items-center justify-between">
              <div className="flex items-center gap-3">
                <svg className="w-5 h-5 text-gray-400" fill="currentColor" viewBox="0 0 24 24">
                  <path d="M12 14c-1.66 0-3-1.34-3-3V5c0-1.66 1.34-3 3-3s3 1.34 3 3v6c0 1.66-1.34 3-3 3z"/>
                  <path d="M19 11c0 .55-.45 1-1 1s-1-.45-1-1c0-2.76-2.24-5-5-5s-5 2.24-5 5c0 .55-.45 1-1 1s-1-.45-1-1c0-3.87 3.13-7 7-7s7 3.13 7 7z"/>
                </svg>
                <div>
                  <p className="font-medium">Reconnaissance vocale</p>
                  <p className="text-sm text-gray-600">Recherche par voix</p>
                </div>
              </div>
              <Switch
                checked={voiceEnabled}
                onCheckedChange={setVoiceEnabled}
              />
            </div>

            <Separator />

            <div className="flex items-center justify-between">
              <div className="flex items-center gap-3">
                <svg className="w-5 h-5 text-gray-400" fill="currentColor" viewBox="0 0 24 24">
                  <path d="M12 2C6.48 2 2 6.48 2 12s4.48 10 10 10 10-4.48 10-10S17.52 2 12 2zm-2 15l-5-5 1.41-1.41L10 14.17l7.59-7.59L19 8l-9 9z"/>
                </svg>
                <div>
                  <p className="font-medium">Mode données réduites</p>
                  <p className="text-sm text-gray-600">Économiser la bande passante</p>
                </div>
              </div>
              <Switch
                checked={lowDataMode}
                onCheckedChange={setLowDataMode}
              />
            </div>
          </CardContent>
        </Card>

        {/* Account Actions */}
        <Card>
          <CardContent className="p-4 space-y-3">
            <Button variant="ghost" className="w-full justify-start gap-3">
              <Shield className="w-5 h-5 text-gray-400" />
              Sécurité et confidentialité
            </Button>

            <Button variant="ghost" className="w-full justify-start gap-3">
              <HelpCircle className="w-5 h-5 text-gray-400" />
              Aide et support
            </Button>

            <Separator />

            <Button 
              variant="ghost" 
              className="w-full justify-start gap-3 text-red-600 hover:text-red-700 hover:bg-red-50"
              onClick={handleLogout}
            >
              <LogOut className="w-5 h-5" />
              Se déconnecter
            </Button>
          </CardContent>
        </Card>

        {/* App Info */}
        <div className="text-center text-sm text-gray-500 space-y-1">
          <p>WagaRaaga v1.0.0</p>
          <p>Membre depuis {new Date(userProfile.createdAt).toLocaleDateString('fr-FR', { month: 'long', year: 'numeric' })}</p>
        </div>
      </div>
    </div>
  );
}

