import React, { useState } from 'react';
import { Button } from '@/components/ui/button';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui/tabs';
import { useSupabaseAuth } from '@/hooks/use-supabase-auth';
import { useSimpleAuth } from '@/hooks/use-simple-auth';
import { FaGoogle, FaFacebook } from 'react-icons/fa';
import { Store, User } from 'lucide-react';

export function FreeAuth() {
  const [useSupabase, setUseSupabase] = useState(false);
  const supabaseAuth = useSupabaseAuth();
  const simpleAuth = useSimpleAuth();

  const [formData, setFormData] = useState({
    username: '',
    fullName: '',
    isVendor: false
  });

  const handleSimpleLogin = () => {
    if (!formData.username || !formData.fullName) return;
    simpleAuth.login(formData);
  };

  const handleSimpleRegister = () => {
    if (!formData.username || !formData.fullName) return;
    simpleAuth.register(formData);
  };

  return (
    <div className="w-full max-w-md mx-auto">
      <Card>
        <CardHeader className="text-center">
          <div className="w-16 h-16 bg-gradient-to-r from-[#E67E22] to-[#F39C12] rounded-2xl mx-auto mb-4 flex items-center justify-center">
            <Store className="w-8 h-8 text-white" />
          </div>
          <CardTitle className="text-2xl font-bold text-[#2C3E50]">WagaRaaga</CardTitle>
          <p className="text-gray-600">Choisissez votre méthode d'authentification</p>
        </CardHeader>

        <CardContent>
          <Tabs defaultValue="simple" className="w-full">
            <TabsList className="grid w-full grid-cols-2">
              <TabsTrigger value="simple">Connexion Simple</TabsTrigger>
              <TabsTrigger value="social">Réseaux Sociaux</TabsTrigger>
            </TabsList>

            <TabsContent value="simple" className="space-y-4">
              <div className="space-y-2">
                <Label htmlFor="username">Nom d'utilisateur</Label>
                <Input
                  id="username"
                  placeholder="Entrez votre nom d'utilisateur"
                  value={formData.username}
                  onChange={(e) => setFormData({...formData, username: e.target.value})}
                />
              </div>

              <div className="space-y-2">
                <Label htmlFor="fullName">Nom complet</Label>
                <Input
                  id="fullName"
                  placeholder="Entrez votre nom complet"
                  value={formData.fullName}
                  onChange={(e) => setFormData({...formData, fullName: e.target.value})}
                />
              </div>

              <div className="flex items-center space-x-2">
                <input
                  type="checkbox"
                  id="isVendor"
                  checked={formData.isVendor}
                  onChange={(e) => setFormData({...formData, isVendor: e.target.checked})}
                  className="rounded"
                />
                <Label htmlFor="isVendor">Je suis un vendeur</Label>
              </div>

              <div className="space-y-2">
                <Button 
                  onClick={handleSimpleLogin}
                  className="w-full bg-[#E67E22] hover:bg-[#D35400]"
                  disabled={!formData.username || !formData.fullName}
                >
                  Se connecter
                </Button>

                <Button 
                  onClick={handleSimpleRegister}
                  variant="outline"
                  className="w-full"
                  disabled={!formData.username || !formData.fullName}
                >
                  Créer un compte
                </Button>
              </div>
            </TabsContent>

            <TabsContent value="social" className="space-y-4">
              <div className="text-center mb-4">
                <p className="text-sm text-gray-600">
                  Authentification via Supabase (gratuit jusqu'à 50,000 utilisateurs)
                </p>
              </div>

              <Button
                onClick={supabaseAuth.signInWithGoogle}
                variant="outline"
                className="w-full flex items-center justify-center gap-2"
                disabled={supabaseAuth.loading}
              >
                <FaGoogle className="w-5 h-5 text-red-500" />
                Continuer avec Google
              </Button>

              <Button
                onClick={supabaseAuth.signInWithFacebook}
                variant="outline"
                className="w-full flex items-center justify-center gap-2"
                disabled={supabaseAuth.loading}
              >
                <FaFacebook className="w-5 h-5 text-blue-600" />
                Continuer avec Facebook
              </Button>

              <div className="text-xs text-gray-500 text-center">
                Configuration Supabase requise dans les variables d'environnement
              </div>
            </TabsContent>
          </Tabs>
        </CardContent>
      </Card>
    </div>
  );
}

