import Header from "@/components/Header";
import BottomNav from "@/components/BottomNav";
import SOSButton from "@/components/SOSButton";
import GoogleMap from "@/components/GoogleMap";
import FilterChips from "@/components/FilterChips";
import { Card } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { useState } from "react";
import { useQuery } from "@tanstack/react-query";
import { Loader2, BarChart3 } from "lucide-react";
import type { Categorie, Signalement } from "@shared/schema";

export default function Carte() {
  const [filter, setFilter] = useState<Categorie | "tous">("tous");
  const [showStats, setShowStats] = useState(false);

  const { data: signalements, isLoading } = useQuery<Signalement[]>({
    queryKey: ["/api/signalements"],
  });

  const markers = signalements?.map(s => ({
    id: s.id,
    lat: parseFloat(s.latitude),
    lng: parseFloat(s.longitude),
    categorie: s.categorie as Categorie,
    titre: s.titre,
    isSOS: s.isSOS || false,
    niveauUrgence: s.niveauUrgence,
  })) || [];

  const filteredMarkers = filter === "tous" 
    ? markers 
    : markers.filter(m => m.categorie === filter);

  const sosCount = filteredMarkers.filter(m => m.isSOS).length;
  const urgencyStats = {
    faible: filteredMarkers.filter(m => m.niveauUrgence === 'faible').length,
    moyen: filteredMarkers.filter(m => m.niveauUrgence === 'moyen').length,
    critique: filteredMarkers.filter(m => m.niveauUrgence === 'critique').length,
  };

  if (isLoading) {
    return (
      <div className="min-h-screen bg-background pb-24">
        <Header />
        <div className="flex items-center justify-center h-[calc(100vh-4rem)]">
          <Loader2 className="w-8 h-8 animate-spin text-primary" />
        </div>
        <BottomNav />
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-background pb-24">
      <Header />

      <div className="relative h-[calc(100vh-4rem-4rem)] md:h-[calc(100vh-4rem)]">
        <div className="absolute top-2 left-2 right-2 md:top-4 md:left-4 md:right-4 z-30 max-w-md">
          <Card className="p-3 md:p-4 bg-background/95 backdrop-blur supports-[backdrop-filter]:bg-background/90 shadow-lg border-2">
            <div className="flex flex-wrap items-center justify-between gap-2 mb-3">
              <h2 className="text-base md:text-lg font-semibold">Carte Interactive</h2>
              <Button
                variant="ghost"
                size="icon"
                onClick={() => setShowStats(!showStats)}
                title="Afficher les statistiques"
                data-testid="button-toggle-stats"
              >
                <BarChart3 className="w-4 h-4" />
              </Button>
            </div>

            <FilterChips onFilterChange={setFilter} />

            {showStats && (
              <div className="mt-3 p-3 bg-primary/10 rounded-md border border-primary/30 animate-in slide-in-from-top-2 duration-300">
                <div className="flex items-center gap-2 mb-2">
                  <BarChart3 className="w-4 h-4 text-primary" />
                  <p className="text-xs font-medium text-primary">Statistiques en temps réel</p>
                </div>
                <div className="grid grid-cols-2 gap-2 text-xs">
                  <div className="flex items-center gap-2 p-2 bg-background/60 rounded">
                    <span className="font-semibold">{filteredMarkers.length}</span>
                    <span className="text-muted-foreground">Total</span>
                  </div>
                  {sosCount > 0 && (
                    <div className="flex items-center gap-2 p-2 bg-red-500/10 rounded border border-red-500/30">
                      <span className="font-semibold text-red-600">{sosCount}</span>
                      <span className="text-red-600">SOS</span>
                    </div>
                  )}
                  <div className="flex items-center gap-2 p-2 bg-background/60 rounded">
                    <div className="w-2 h-2 rounded-full bg-green-500" />
                    <span className="font-semibold">{urgencyStats.faible}</span>
                    <span className="text-muted-foreground">Faible</span>
                  </div>
                  <div className="flex items-center gap-2 p-2 bg-background/60 rounded">
                    <div className="w-2 h-2 rounded-full bg-orange-500" />
                    <span className="font-semibold">{urgencyStats.moyen}</span>
                    <span className="text-muted-foreground">Moyen</span>
                  </div>
                  <div className="flex items-center gap-2 p-2 bg-background/60 rounded col-span-2">
                    <div className="w-2 h-2 rounded-full bg-red-500" />
                    <span className="font-semibold">{urgencyStats.critique}</span>
                    <span className="text-muted-foreground">Critique</span>
                  </div>
                </div>
              </div>
            )}

            <p className="text-xs text-muted-foreground mt-3 flex items-center gap-2">
              <span className="inline-block w-2 h-2 rounded-full bg-primary animate-pulse" />
              {filteredMarkers.length} signalement{filteredMarkers.length > 1 ? "s" : ""} affiché{filteredMarkers.length > 1 ? "s" : ""}
            </p>
          </Card>
        </div>

        <GoogleMap markers={filteredMarkers} className="h-full" />
      </div>

      <SOSButton onClick={() => window.location.href = "/sos/publier"} />
      <BottomNav />
    </div>
  );
}

BurkinaWatch/shared/schema.ts.content:
import { sql } from "drizzle-orm";
import { pgTable, text, varchar, timestamp, boolean, integer, decimal, jsonb, index } from "drizzle-orm/pg-core";
import { createInsertSchema } from "drizzle-zod";
import { z } from "zod";

export const sessions = pgTable(
  "sessions",
  {
    sid: varchar("sid").primaryKey(),
    sess: jsonb("sess").notNull(),
    expire: timestamp("expire").notNull(),
  },
  (table) => [index("IDX_session_expire").on(table.expire)],
);

export const users = pgTable("users", {
  id: varchar("id").primaryKey(),
  email: varchar("email").unique(),
  firstName: varchar("first_name"),
  lastName: varchar("last_name"),
  profileImageUrl: varchar("profile_image_url"),
  telephone: varchar("telephone"),
  bio: text("bio"),
  ville: varchar("ville"),
  role: varchar("role").default("citoyen"),
  emailTrackingEnabled: boolean("email_tracking_enabled").default(false),
  createdAt: timestamp("created_at").defaultNow(),
  updatedAt: timestamp("updated_at").defaultNow(),
});

export const signalements = pgTable("signalements", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  titre: text("titre").notNull(),
  description: text("description").notNull(),
  categorie: text("categorie").notNull(),
  latitude: decimal("latitude", { precision: 10, scale: 7 }).notNull(),
  longitude: decimal("longitude", { precision: 10, scale: 7 }).notNull(),
  localisation: text("localisation"),
  photo: text("photo"),
  video: text("video"),
  userId: varchar("user_id"),
  isAnonymous: boolean("is_anonymous").default(false),
  isSOS: boolean("is_sos").default(false),
  niveauUrgence: text("niveau_urgence"),
  statut: text("statut").default("en_attente"),
  likes: integer("likes").default(0),
  commentairesCount: integer("commentaires_count").default(0),
  sharesCount: integer("shares_count").default(0),
  createdAt: timestamp("created_at").defaultNow(),
});

export const commentaires = pgTable("commentaires", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  signalementId: varchar("signalement_id").references(() => signalements.id).notNull(),
  userId: varchar("user_id").references(() => users.id),
  contenu: text("contenu").notNull(),
  createdAt: timestamp("created_at").defaultNow(),
});

export const trackingSessions = pgTable("tracking_sessions", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  userId: varchar("user_id").references(() => users.id).notNull(),
  startTime: timestamp("start_time").defaultNow().notNull(),
  endTime: timestamp("end_time"),
  isActive: boolean("is_active").default(true).notNull(),
});

export const locationPoints = pgTable("location_points", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  sessionId: varchar("session_id").references(() => trackingSessions.id).notNull(),
  userId: varchar("user_id").references(() => users.id).notNull(),
  latitude: decimal("latitude", { precision: 10, scale: 7 }).notNull(),
  longitude: decimal("longitude", { precision: 10, scale: 7 }).notNull(),
  accuracy: decimal("accuracy", { precision: 10, scale: 2 }),
  timestamp: timestamp("timestamp").defaultNow().notNull(),
});

export const upsertUserSchema = createInsertSchema(users).omit({
  createdAt: true,
  updatedAt: true,
  role: true,
});

export const updateUserProfileSchema = createInsertSchema(users).omit({
  id: true,
  email: true,
  createdAt: true,
  updatedAt: true,
  role: true,
}).partial();

export const insertSignalementSchema = createInsertSchema(signalements, {
  latitude: z.union([z.string(), z.number()]).transform(val => String(val)),
  longitude: z.union([z.string(), z.number()]).transform(val => String(val)),
  photo: z.string().optional(),
  video: z.string().optional(),
  localisation: z.string().optional(),
  niveauUrgence: z.string().optional(),
}).omit({
  id: true,
  createdAt: true,
  likes: true,
  commentairesCount: true,
  sharesCount: true,
  statut: true,
});

export const updateSignalementSchema = createInsertSchema(signalements, {
  latitude: z.union([z.string(), z.number()]).transform(val => String(val)),
  longitude: z.union([z.string(), z.number()]).transform(val => String(val)),
  photo: z.string().optional(),
  video: z.string().optional(),
  localisation: z.string().optional(),
  niveauUrgence: z.string().optional(),
  statut: z.enum(["en_attente", "en_cours", "resolu", "rejete"]).optional(),
}).omit({
  id: true,
  createdAt: true,
  likes: true,
  commentairesCount: true,
  sharesCount: true,
  userId: true,
  isAnonymous: true,
  isSOS: true,
}).partial();

export const insertCommentaireSchema = createInsertSchema(commentaires).omit({
  id: true,
  createdAt: true,
});

export const insertTrackingSessionSchema = createInsertSchema(trackingSessions).omit({
  id: true,
  startTime: true,
  isActive: true,
});

export const insertLocationPointSchema = createInsertSchema(locationPoints, {
  latitude: z.union([z.string(), z.number()]).transform(val => String(val)),
  longitude: z.union([z.string(), z.number()]).transform(val => String(val)),
  accuracy: z.union([z.string(), z.number()]).transform(val => String(val)).optional(),
}).omit({
  id: true,
  timestamp: true,
});

export type UpsertUser = z.infer<typeof upsertUserSchema>;
export type UpdateUserProfile = z.infer<typeof updateUserProfileSchema>;
export type User = typeof users.$inferSelect;
export type InsertSignalement = z.infer<typeof insertSignalementSchema>;
export type UpdateSignalement = z.infer<typeof updateSignalementSchema>;
export type Signalement = typeof signalements.$inferSelect;
export type InsertCommentaire = z.infer<typeof insertCommentaireSchema>;
export type Commentaire = typeof commentaires.$inferSelect;
export type InsertTrackingSession = z.infer<typeof insertTrackingSessionSchema>;
export type TrackingSession = typeof trackingSessions.$inferSelect;
export type InsertLocationPoint = z.infer<typeof insertLocationPointSchema>;
export type LocationPoint = typeof locationPoints.$inferSelect;

export type SignalementWithAuthor = Signalement & {
  auteurFirstName?: string | null;
  auteurLastName?: string | null;
};

export type TrackingSessionWithTrajectory = TrackingSession & {
  trajectoryUrl: string | null;
  pointCount: number;
};

export const categories = ["urgence", "securite", "sante", "environnement", "corruption", "infrastructure", "personne_recherchee"] as const;
export const statuts = ["en_attente", "en_cours", "resolu", "rejete"] as const;
export const niveauxUrgence = ["faible", "moyen", "critique"] as const;

export type Categorie = typeof categories[number];
export type Statut = typeof statuts[number];
export type NiveauUrgence = typeof niveauxUrgence[number];

