import { useState, useEffect } from "react";
import { useMutation, useQuery, useQueryClient } from "@tanstack/react-query";
import { Header } from "@/components/layout/header";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { Label } from "@/components/ui/label";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { MapsLoader } from "@/components/maps/maps-loader";
import { GoogleMap } from "@/components/maps/google-map";
import { Camera, MapPin, Phone, Upload, X, Check, Map } from "lucide-react";
import { useGeolocation } from "@/hooks/use-geolocation";
import { useGoogleMaps } from "@/hooks/use-google-maps";
import { useToast } from "@/hooks/use-toast";
import { useLocation } from "wouter";

export default function SellPage() {
  const [, setLocation] = useLocation();
  const { latitude, longitude } = useGeolocation();
  const { geocodeLocation } = useGoogleMaps();
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const [formData, setFormData] = useState({
    name: "",
    description: "",
    categoryId: "",
    price: "",
    unit: "",
    imageUrl: "",
    phone: "",
    location: ""
  });

  const [imagePreview, setImagePreview] = useState<string | null>(null);
  const [showMap, setShowMap] = useState(false);
  const [selectedMapLocation, setSelectedMapLocation] = useState<{ lat: number; lng: number } | null>(null);

  const { data: categories } = useQuery({
    queryKey: ["/api/categories"],
  });

  // Géocodage automatique de la position actuelle
  useEffect(() => {
    if (latitude && longitude && !formData.location) {
      geocodeLocation(latitude, longitude).then((address) => {
        if (address) {
          setFormData(prev => ({ ...prev, location: address.formatted_address }));
          setSelectedMapLocation({ lat: latitude, lng: longitude });
        }
      });
    }
  }, [latitude, longitude, geocodeLocation, formData.location]);

  const createProductMutation = useMutation({
    mutationFn: async (productData: any) => {
      const response = await fetch("/api/products", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          ...productData,
          vendorId: "user-1", // Mock user ID
          latitude,
          longitude,
        }),
      });
      if (!response.ok) throw new Error("Erreur lors de la création du produit");
      return response.json();
    },
    onSuccess: () => {
      toast({
        title: "Produit publié avec succès!",
        description: "Votre produit est maintenant visible par tous les utilisateurs.",
      });
      queryClient.invalidateQueries({ queryKey: ["/api/products"] });
      setLocation("/marketplace");
    },
    onError: () => {
      toast({
        title: "Erreur",
        description: "Impossible de publier le produit. Veuillez réessayer.",
        variant: "destructive",
      });
    },
  });

  const handleImageUpload = (e: React.ChangeEvent<HTMLInputElement>) => {
    const file = e.target.files?.[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = (event) => {
        const result = event.target?.result as string;
        setImagePreview(result);
        setFormData(prev => ({ ...prev, imageUrl: result }));
      };
      reader.readAsDataURL(file);
    }
  };

  const handleLocationSelect = async (location: { lat: number; lng: number }) => {
    setSelectedMapLocation(location);
    const address = await geocodeLocation(location.lat, location.lng);
    if (address) {
      setFormData(prev => ({ ...prev, location: address.formatted_address }));
      toast({
        title: "Localisation mise à jour",
        description: `${address.city}, ${address.country}`,
      });
    }
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    if (!formData.name || !formData.price || !formData.categoryId) {
      toast({
        title: "Champs requis manquants",
        description: "Veuillez remplir tous les champs obligatoires.",
        variant: "destructive",
      });
      return;
    }

    const finalLatitude = selectedMapLocation?.lat || latitude;
    const finalLongitude = selectedMapLocation?.lng || longitude;

    createProductMutation.mutate({
      ...formData,
      price: parseFloat(formData.price),
      latitude: finalLatitude,
      longitude: finalLongitude,
    });
  };

  return (
