import type { Express } from "express";
import { createServer, type Server } from "http";
import { storage } from "./storage";
import { 
  insertProductSchema, 
  insertPriceUpdateSchema, 
  insertBarterRequestSchema, 
  insertMessageSchema,
  insertDailyDealSchema,
  insertUserSchema 
} from "@shared/schema";

export async function registerRoutes(app: Express): Promise<Server> {
  // Authentication
  app.post("/api/auth/social-login", async (req, res) => {
    try {
      const firebaseUid = req.headers['x-firebase-uid'] as string;
      if (!firebaseUid) {
        return res.status(400).json({ message: "Firebase UID requis" });
      }

      const { username, fullName, email, phone, location, isVendor, provider, photoURL } = req.body;

      // Vérifier si l'utilisateur existe déjà avec cet UID Firebase
      let user = await storage.getUserByFirebaseUid(firebaseUid);

      if (user) {
        // Mettre à jour les informations si nécessaire
        const updates = {
          fullName: fullName || user.fullName,
          email: email || user.email,
          photoURL: photoURL || user.photoURL
        };
        user = await storage.updateUser(user.id, updates);
      } else {
        // Créer un nouveau utilisateur
        const userData = {
          username: username || email?.split('@')[0] || `user_${Date.now()}`,
          password: '', // Pas de mot de passe pour l'auth sociale
          fullName: fullName || 'Utilisateur',
          email: email || '',
          phone: phone || '',
          location: location || '',
          isVendor: isVendor || false,
          provider: provider || 'social',
          firebaseUid,
          photoURL: photoURL || ''
        };

        user = await storage.createUser(userData);
      }

      const { password, ...userWithoutPassword } = user;
      res.json(userWithoutPassword);
    } catch (error) {
      console.error('Erreur auth sociale:', error);
      res.status(500).json({ message: "Erreur lors de l'authentification sociale" });
    }
  });

  app.post("/api/auth/register", async (req, res) => {
    try {
      const validatedData = insertUserSchema.parse(req.body);

      // Check if user already exists
      const existingUser = await storage.getUserByUsername(validatedData.username);
      if (existingUser) {
        return res.status(400).json({ message: "Nom d'utilisateur déjà utilisé" });
      }

      const user = await storage.createUser(validatedData);
      const { password, ...userWithoutPassword } = user;

      res.json(userWithoutPassword);
    } catch (error) {
      res.status(400).json({ message: "Données utilisateur invalides" });
    }
  });

  app.post("/api/auth/login", async (req, res) => {
    try {
      const { username, password } = req.body;

      if (!username || !password) {
        return res.status(400).json({ message: "Nom d'utilisateur et mot de passe requis" });
      }

      const user = await storage.authenticateUser(username, password);
      if (!user) {
        return res.status(401).json({ message: "Identifiants incorrects" });
      }

      const { password: _, ...userWithoutPassword } = user;
      res.json(userWithoutPassword);
    } catch (error) {
      res.status(500).json({ message: "Erreur de connexion" });
    }
  });

  app.get("/api/auth/me", async (req, res) => {
    try {
      // Vérifier d'abord l'auth Replit
      const replitUserId = req.headers['x-replit-user-id'] as string;
      const replitUserName = req.headers['x-replit-user-name'] as string;
      const replitUserRoles = req.headers['x-replit-user-roles'] as string;

      if (replitUserId && replitUserName) {
        return res.json({
          id: replitUserId,
          name: replitUserName,
          username: replitUserName,
          roles: replitUserRoles ? replitUserRoles.split(',') : [],
          provider: 'replit'
        });
      }

      // Sinon, vérifier l'auth classique
      const userId = req.headers['x-user-id'] as string;
      if (!userId) {
        return res.status(401).json({ message: "Non authentifié" });
      }

      const user = await storage.getUser(userId);
      if (!user) {
        return res.status(404).json({ message: "Utilisateur introuvable" });
      }

      const { password, ...userWithoutPassword } = user;
      res.json(userWithoutPassword);
    } catch (error) {
      res.status(500).json({ message: "Erreur de récupération du profil" });
    }
  });

  app.patch("/api/auth/profile", async (req, res) => {
    try {
      const userId = req.headers['x-user-id'] as string;
      if (!userId) {
        return res.status(401).json({ message: "Non authentifié" });
      }

      const updates = req.body;
      const user = await storage.updateUser(userId, updates);
      if (!user) {
        return res.status(404).json({ message: "Utilisateur introuvable" });
      }

      const { password, ...userWithoutPassword } = user;
      res.json(userWithoutPassword);
    } catch (error) {
      res.status(500).json({ message: "Erreur de mise à jour du profil" });
    }
  });

  // Categories
  app.get("/api/categories", async (req, res) => {
    try {
      const categories = await storage.getCategories();
      res.json(categories);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch categories" });
    }
  });

  // Products
  app.get("/api/products", async (req, res) => {
    try {
      const { categoryId, vendorId, latitude, longitude, radius } = req.query;
      const params = {
        categoryId: categoryId as string,
        vendorId: vendorId as string,
        latitude: latitude ? parseFloat(latitude as string) : undefined,
        longitude: longitude ? parseFloat(longitude as string) : undefined,
        radius: radius ? parseFloat(radius as string) : undefined,
      };

      const products = await storage.getProducts(params);
      res.json(products);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch products" });
    }
  });

  app.get("/api/products/:id", async (req, res) => {
    try {
      const product = await storage.getProduct(req.params.id);
      if (!product) {
        return res.status(404).json({ message: "Product not found" });
      }
      res.json(product);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch product" });
    }
  });

  app.post("/api/products", async (req, res) => {
    try {
      const validatedData = insertProductSchema.parse(req.body);
      const product = await storage.createProduct(validatedData);
      res.json(product);
    } catch (error) {
      res.status(400).json({ message: "Invalid product data" });
    }
  });

  app.patch("/api/products/:id", async (req, res) => {
    try {
      const updates = req.body;
      const product = await storage.updateProduct(req.params.id, updates);
      if (!product) {
        return res.status(404).json({ message: "Product not found" });
      }
      res.json(product);
    } catch (error) {
      res.status(500).json({ message: "Failed to update product" });
    }
  });

  // Price Updates
  app.post("/api/price-updates", async (req, res) => {
    try {
      const validatedData = insertPriceUpdateSchema.parse(req.body);
      const priceUpdate = await storage.createPriceUpdate(validatedData);
      res.json(priceUpdate);
    } catch (error) {
      res.status(400).json({ message: "Invalid price update data" });
    }
  });

  app.get("/api/products/:id/price-updates", async (req, res) => {
    try {
      const priceUpdates = await storage.getProductPriceUpdates(req.params.id);
      res.json(priceUpdates);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch price updates" });
    }
  });

  // Barter Requests
  app.get("/api/barter-requests", async (req, res) => {
    try {
      const { userId } = req.query;
      if (!userId) {
        return res.status(400).json({ message: "userId is required" });
      }
      const barterRequests = await storage.getBarterRequests(userId as string);
      res.json(barterRequests);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch barter requests" });
    }
  });

  app.post("/api/barter-requests", async (req, res) => {
    try {
      const validatedData = insertBarterRequestSchema.parse(req.body);
      const barterRequest = await storage.createBarterRequest(validatedData);
      res.json(barterRequest);
    } catch (error) {
      res.status(400).json({ message: "Invalid barter request data" });
    }
  });

  app.patch("/api/barter-requests/:id", async (req, res) => {
    try {
      const { status } = req.body;
      const barterRequest = await storage.updateBarterRequest(req.params.id, status);
      if (!barterRequest) {
        return res.status(404).json({ message: "Barter request not found" });
      }
      res.json(barterRequest);
    } catch (error) {
      res.status(500).json({ message: "Failed to update barter request" });
    }
  });

  // Messages
  app.get("/api/conversations", async (req, res) => {
    try {
      const { userId1, userId2, productId } = req.query;
      if (!userId1 || !userId2) {
        return res.status(400).json({ message: "userId1 and userId2 are required" });
      }
      const messages = await storage.getConversation(
        userId1 as string, 
        userId2 as string, 
        productId as string
      );
      res.json(messages);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch conversation" });
    }
  });

  app.post("/api/messages", async (req, res) => {
    try {
      const validatedData = insertMessageSchema.parse(req.body);
      const message = await storage.createMessage(validatedData);
      res.json(message);
    } catch (error) {
      res.status(400).json({ message: "Invalid message data" });
    }
  });

  // Daily Deals
  app.get("/api/daily-deals", async (req, res) => {
    try {
      const { latitude, longitude } = req.query;
      const deals = await storage.getDailyDeals(
        latitude ? parseFloat(latitude as string) : undefined,
        longitude ? parseFloat(longitude as string) : undefined
      );
      res.json(deals);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch daily deals" });
    }
  });

  app.post("/api/daily-deals", async (req, res) => {
    try {
      const validatedData = insertDailyDealSchema.parse(req.body);
      const dailyDeal = await storage.createDailyDeal(validatedData);
      res.json(dailyDeal);
    } catch (error) {
      res.status(400).json({ message: "Invalid daily deal data" });
    }
  });

  // Search
  app.get("/api/search", async (req, res) => {
    try {
      const { q, latitude, longitude } = req.query;
      if (!q) {
        return res.status(400).json({ message: "Search query is required" });
      }

      // Simple search implementation
      const params = {
        latitude: latitude ? parseFloat(latitude as string) : undefined,
        longitude: longitude ? parseFloat(longitude as string) : undefined,
        radius: 50, // 50km radius
      };

      const allProducts = await storage.getProducts(params);
      const searchTerm = (q as string).toLowerCase();

      const results = allProducts.filter(product => 
        product.name.toLowerCase().includes(searchTerm) ||
        product.description?.toLowerCase().includes(searchTerm) ||
        product.category?.name.toLowerCase().includes(searchTerm)
      );

      res.json(results);
    } catch (error) {
      res.status(500).json({ message: "Failed to perform search" });
    }
  });

  const httpServer = createServer(app);
  return httpServer;
}

