import { useState, useCallback } from "react";
import { useToast } from "@/hooks/use-toast";

interface PlacePrediction {
  place_id: string;
  description: string;
  structured_formatting: {
    main_text: string;
    secondary_text: string;
  };
}

interface UsePlacesAutocompleteReturn {
  predictions: PlacePrediction[];
  getPlacePredictions: (input: string) => Promise<void>;
  getPlaceDetails: (placeId: string) => Promise<google.maps.places.PlaceResult | null>;
  isLoading: boolean;
  error: string | null;
}

export function usePlacesAutocomplete(): UsePlacesAutocompleteReturn {
  const [predictions, setPredictions] = useState<PlacePrediction[]>([]);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const { toast } = useToast();

  const getPlacePredictions = useCallback(async (input: string) => {
    if (!window.google || !input.trim()) {
      setPredictions([]);
      return;
    }

    setIsLoading(true);
    setError(null);

    try {
      const service = new google.maps.places.AutocompleteService();

      const request = {
        input: input,
        componentRestrictions: { country: 'BF' },
        language: 'fr',
        types: ['establishment', 'geocode']
      };

      service.getPlacePredictions(request, (predictions, status) => {
        if (status === google.maps.places.PlacesServiceStatus.OK && predictions) {
          setPredictions(predictions.map(p => ({
            place_id: p.place_id,
            description: p.description,
            structured_formatting: p.structured_formatting
          })));
        } else {
          setPredictions([]);
          if (status !== google.maps.places.PlacesServiceStatus.ZERO_RESULTS) {
            setError(`Erreur de recherche: ${status}`);
          }
        }
        setIsLoading(false);
      });
    } catch (err) {
      const errorMessage = err instanceof Error ? err.message : 'Erreur de recherche';
      setError(errorMessage);
      setPredictions([]);
      setIsLoading(false);
      toast({
        title: "Erreur de recherche",
        description: errorMessage,
        variant: "destructive",
      });
    }
  }, [toast]);

  const getPlaceDetails = useCallback(async (placeId: string): Promise<google.maps.places.PlaceResult | null> => {
    if (!window.google) {
      setError("Google Maps non disponible");
      return null;
    }

    setIsLoading(true);
    setError(null);

    try {
      const service = new google.maps.places.PlacesService(
        document.createElement('div')
      );

      return new Promise((resolve, reject) => {
        service.getDetails({
          placeId: placeId,
          fields: ['name', 'formatted_address', 'geometry', 'place_id', 'types']
        }, (place, status) => {
          if (status === google.maps.places.PlacesServiceStatus.OK && place) {
            resolve(place);
          } else {
            reject(new Error(`Erreur de détails: ${status}`));
          }
          setIsLoading(false);
        });
      });
    } catch (err) {
      const errorMessage = err instanceof Error ? err.message : 'Erreur de détails';
      setError(errorMessage);
      setIsLoading(false);
      toast({
        title: "Erreur de détails",
        description: errorMessage,
        variant: "destructive",
      });
      return null;
    }
  }, [toast]);

  return {
    predictions,
    getPlacePredictions,
    getPlaceDetails,
    isLoading,
    error,
  };
}

