import { useState } from "react";
import { useQuery } from "@tanstack/react-query";
import { Header } from "@/components/layout/header";
import { ProductCard } from "@/components/product/product-card";
import { CategoryGrid } from "@/components/product/category-grid";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Skeleton } from "@/components/ui/skeleton";
import { Badge } from "@/components/ui/badge";
import { Search, Filter, MapPin } from "lucide-react";
import { useGeolocation } from "@/hooks/use-geolocation";
import { useToast } from "@/hooks/use-toast";
import type { ProductWithDetails } from "@shared/schema";

export default function SearchPage() {
  const [searchQuery, setSearchQuery] = useState("");
  const [selectedCategory, setSelectedCategory] = useState<string>("");
  const [showFilters, setShowFilters] = useState(false);
  const { latitude, longitude, error: locationError } = useGeolocation();
  const { toast } = useToast();

  const { data: searchResults, isLoading } = useQuery({
    queryKey: ["/api/search", searchQuery, latitude, longitude],
    enabled: searchQuery.length > 0 && latitude !== null && longitude !== null,
  });

  const { data: allProducts } = useQuery({
    queryKey: ["/api/products", selectedCategory, latitude, longitude],
    enabled: latitude !== null && longitude !== null,
  });

  const displayProducts = searchQuery ? searchResults : allProducts;

  const handleSearch = (query: string) => {
    setSearchQuery(query);
  };

  const handleCategorySelect = (categoryId: string) => {
    setSelectedCategory(categoryId);
    setSearchQuery("");
  };

  const handleProductAction = (product: ProductWithDetails, action: string) => {
    toast({
      title: `Action: ${action}`,
      description: `${action} pour ${product.name}`,
    });
  };

  return (
    <div className="min-h-screen bg-gray-50">
      <Header onSearch={handleSearch} />

      <div className="pb-20">
        {/* Search Filters */}
        <div className="bg-white border-b border-gray-200 px-4 py-4">
          <div className="flex items-center gap-3 mb-3">
            <div className="relative flex-1">
              <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 w-4 h-4" />
              <Input
                type="text"
                placeholder="Rechercher produits, vendeurs..."
                value={searchQuery}
                onChange={(e) => setSearchQuery(e.target.value)}
                className="pl-10"
              />
            </div>
            <Button
              size="icon"
              variant="outline"
              onClick={() => setShowFilters(!showFilters)}
            >
              <Filter className="w-4 h-4" />
            </Button>
          </div>

          {/* Location Status */}
          {locationError ? (
            <div className="flex items-center gap-2 text-sm text-amber-600 bg-amber-50 px-3 py-2 rounded-lg">
              <MapPin className="w-4 h-4" />
              <span>Localisation désactivée - résultats limités</span>
            </div>
          ) : (
            <div className="flex items-center gap-2 text-sm text-green-600 bg-green-50 px-3 py-2 rounded-lg">
              <MapPin className="w-4 h-4" />
              <span>Recherche basée sur votre localisation</span>
            </div>
          )}
        </div>

        {/* Active Filters */}
        {(searchQuery || selectedCategory) && (
          <div className="px-4 py-3 bg-white border-b border-gray-100">
            <div className="flex items-center gap-2 flex-wrap">
              <span className="text-sm text-gray-600">Filtres actifs:</span>
              {searchQuery && (
                <Badge 
                  variant="secondary" 
                  className="cursor-pointer"
                  onClick={() => setSearchQuery("")}
                >
                  "{searchQuery}" ×
                </Badge>
              )}
              {selectedCategory && (
                <Badge 
                  variant="secondary"
                  className="cursor-pointer"
                  onClick={() => setSelectedCategory("")}
                >
                  Catégorie ×
                </Badge>
              )}
            </div>
          </div>
        )}

        {/* Categories - show when no search query */}
        {!searchQuery && (
          <CategoryGrid onCategorySelect={handleCategorySelect} />
        )}

        {/* Search Results / Products */}
        <div className="px-4 py-4">
          <div className="flex items-center justify-between mb-4">
            <h2 className="text-lg font-semibold">
              {searchQuery 
                ? `Résultats pour "${searchQuery}"` 
                : selectedCategory 
                  ? "Produits de la catégorie"
                  : "Tous les produits"
              }
            </h2>
            {displayProducts && (
              <span className="text-sm text-gray-500">
                {displayProducts.length} produit{displayProducts.length !== 1 ? 's' : ''}
              </span>
            )}
          </div>

          {isLoading ? (
            <div className="space-y-4">
              {Array.from({ length: 6 }).map((_, i) => (
                <div key={i} className="border rounded-2xl overflow-hidden">
                  <Skeleton className="w-full h-40" />
                  <div className="p-4 space-y-2">
                    <Skeleton className="h-4 w-3/4" />
                    <Skeleton className="h-3 w-1/2" />
                    <div className="flex gap-2">
                      <Skeleton className="h-8 flex-1" />
                      <Skeleton className="h-8 w-12" />
                      <Skeleton className="h-8 w-12" />
                    </div>
                  </div>
                </div>
              ))}
            </div>
          ) : displayProducts && displayProducts.length > 0 ? (
            <div className="space-y-4">
              {displayProducts.map((product: ProductWithDetails) => (
                <ProductCard
                  key={product.id}
                  product={product}
                  onBarter={(product) => handleProductAction(product, "Troc")}
                  onVoiceChat={(product) => handleProductAction(product, "Chat vocal")}
                  onShare={(product) => handleProductAction(product, "Partage")}
                />
              ))}
            </div>
          ) : (
            <div className="text-center py-12">
              <div className="w-16 h-16 bg-gray-100 rounded-full flex items-center justify-center mx-auto mb-4">
                <Search className="w-8 h-8 text-gray-400" />
              </div>
              <h3 className="text-lg font-semibold text-gray-900 mb-2">
                Aucun résultat trouvé
              </h3>
              <p className="text-gray-600 mb-4">
                {searchQuery 
                  ? `Aucun produit trouvé pour "${searchQuery}"`
                  : "Aucun produit disponible dans cette catégorie"
                }
              </p>
              <div className="space-y-2">
                <Button 
                  variant="outline" 
                  onClick={() => {
                    setSearchQuery("");
                    setSelectedCategory("");
                  }}
                >
                  Réinitialiser les filtres
                </Button>
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

