import { sql } from "drizzle-orm";
import { pgTable, text, varchar, integer, decimal, timestamp, boolean } from "drizzle-orm/pg-core";
import { createInsertSchema } from "drizzle-zod";
import { z } from "zod";

export const users = pgTable("users", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  username: text("username").notNull().unique(),
  password: text("password").notNull(),
  fullName: text("full_name").notNull(),
  phone: text("phone"),
  location: text("location"),
  isVendor: boolean("is_vendor").default(false),
  createdAt: timestamp("created_at").defaultNow(),
});

export const categories = pgTable("categories", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  name: text("name").notNull(),
  icon: text("icon").notNull(),
  color: text("color").notNull(),
});

export const products = pgTable("products", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  name: text("name").notNull(),
  description: text("description"),
  categoryId: varchar("category_id").references(() => categories.id),
  vendorId: varchar("vendor_id").references(() => users.id),
  price: decimal("price", { precision: 10, scale: 2 }).notNull(),
  unit: text("unit").notNull(), // kg, m, piece, etc.
  imageUrl: text("image_url"),
  latitude: decimal("latitude", { precision: 10, scale: 8 }),
  longitude: decimal("longitude", { precision: 11, scale: 8 }),
  isAvailable: boolean("is_available").default(true),
  rating: decimal("rating", { precision: 3, scale: 2 }).default("0"),
  reviewCount: integer("review_count").default(0),
  createdAt: timestamp("created_at").defaultNow(),
  updatedAt: timestamp("updated_at").defaultNow(),
});

export const priceUpdates = pgTable("price_updates", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  productId: varchar("product_id").references(() => products.id),
  userId: varchar("user_id").references(() => users.id),
  newPrice: decimal("new_price", { precision: 10, scale: 2 }).notNull(),
  oldPrice: decimal("old_price", { precision: 10, scale: 2 }),
  photoUrl: text("photo_url"),
  verified: boolean("verified").default(false),
  createdAt: timestamp("created_at").defaultNow(),
});

export const barterRequests = pgTable("barter_requests", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  requesterId: varchar("requester_id").references(() => users.id),
  offerProductId: varchar("offer_product_id").references(() => products.id),
  requestedProductId: varchar("requested_product_id").references(() => products.id),
  offerQuantity: decimal("offer_quantity", { precision: 10, scale: 2 }).notNull(),
  requestedQuantity: decimal("requested_quantity", { precision: 10, scale: 2 }).notNull(),
  status: text("status").default("pending"), // pending, accepted, rejected, completed
  message: text("message"),
  createdAt: timestamp("created_at").defaultNow(),
});

export const messages = pgTable("messages", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  senderId: varchar("sender_id").references(() => users.id),
  receiverId: varchar("receiver_id").references(() => users.id),
  productId: varchar("product_id").references(() => products.id),
  content: text("content").notNull(),
  isVoiceMessage: boolean("is_voice_message").default(false),
  voiceUrl: text("voice_url"),
  createdAt: timestamp("created_at").defaultNow(),
});

export const dailyDeals = pgTable("daily_deals", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  productId: varchar("product_id").references(() => products.id),
  originalPrice: decimal("original_price", { precision: 10, scale: 2 }).notNull(),
  dealPrice: decimal("deal_price", { precision: 10, scale: 2 }).notNull(),
  discountPercentage: integer("discount_percentage").notNull(),
  validUntil: timestamp("valid_until").notNull(),
  createdAt: timestamp("created_at").defaultNow(),
});

// Insert schemas
export const insertUserSchema = createInsertSchema(users).omit({
  id: true,
  createdAt: true,
}).extend({
  username: z.string().min(3, "Le nom d'utilisateur doit contenir au moins 3 caractères"),
  password: z.string().min(6, "Le mot de passe doit contenir au moins 6 caractères").optional(),
  fullName: z.string().min(2, "Le nom complet doit contenir au moins 2 caractères"),
  email: z.string().email("Email invalide").optional(),
  phone: z.string().optional(),
  location: z.string().optional(),
  isVendor: z.boolean().default(false),
  provider: z.string().optional(),
  firebaseUid: z.string().optional(),
  photoURL: z.string().optional(),
});

export const insertCategorySchema = createInsertSchema(categories).omit({
  id: true,
});

export const insertProductSchema = createInsertSchema(products).omit({
  id: true,
  createdAt: true,
  updatedAt: true,
});

export const insertPriceUpdateSchema = createInsertSchema(priceUpdates).omit({
  id: true,
  createdAt: true,
});

export const insertBarterRequestSchema = createInsertSchema(barterRequests).omit({
  id: true,
  createdAt: true,
});

export const insertMessageSchema = createInsertSchema(messages).omit({
  id: true,
  createdAt: true,
});

export const insertDailyDealSchema = createInsertSchema(dailyDeals).omit({
  id: true,
  createdAt: true,
});

// Types
export type InsertUser = z.infer<typeof insertUserSchema>;
export type User = typeof users.$inferSelect;

export type InsertCategory = z.infer<typeof insertCategorySchema>;
export type Category = typeof categories.$inferSelect;

export type InsertProduct = z.infer<typeof insertProductSchema>;
export type Product = typeof products.$inferSelect;

export type InsertPriceUpdate = z.infer<typeof insertPriceUpdateSchema>;
export type PriceUpdate = typeof priceUpdates.$inferSelect;

export type InsertBarterRequest = z.infer<typeof insertBarterRequestSchema>;
export type BarterRequest = typeof barterRequests.$inferSelect;

export type InsertMessage = z.infer<typeof insertMessageSchema>;
export type Message = typeof messages.$inferSelect;

export type InsertDailyDeal = z.infer<typeof insertDailyDealSchema>;
export type DailyDeal = typeof dailyDeals.$inferSelect;

// Extended types for API responses
export type ProductWithDetails = Product & {
  category?: Category;
  vendor?: User;
  distance?: number;
  lastPriceUpdate?: PriceUpdate;
};

export type BarterRequestWithDetails = BarterRequest & {
  requester?: User;
  offerProduct?: Product;
  requestedProduct?: Product;
};

export type MessageWithDetails = Message & {
  sender?: User;
