import { Button } from "@/components/ui/button";
import { Card } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { AlertTriangle, AlertCircle, Loader2, Radio } from "lucide-react";
import { useState, useEffect } from "react";
import { useMutation, useQuery } from "@tanstack/react-query";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import { useLocation } from "wouter";
import type { EmergencyContact } from "@shared/schema";

export default function EmergencyPanel() {
  const { toast } = useToast();
  const [, setLocation] = useLocation();
  const [pressCount, setPressCount] = useState(0);
  const [lastPressTime, setLastPressTime] = useState(0);
  const [isActivating, setIsActivating] = useState(false);

  const { data: contacts, isLoading: contactsLoading } = useQuery<EmergencyContact[]>({
    queryKey: ["/api/emergency-contacts"],
  });

  const panicMutation = useMutation({
    mutationFn: async () => {
      const position = await new Promise<GeolocationPosition>((resolve, reject) => {
        navigator.geolocation.getCurrentPosition(resolve, reject);
      });

      const res = await apiRequest("POST", "/api/panic-alert", {
        latitude: position.coords.latitude.toString(),
        longitude: position.coords.longitude.toString(),
      });
      return res.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/panic-alerts"] });
      queryClient.invalidateQueries({ queryKey: ["/api/notifications/unread-count"] });
      toast({
        title: "🚨 Alerte envoyée",
        description: `Vos contacts d'urgence ont été notifiés de votre position.`,
        variant: "destructive",
      });
      setIsActivating(false);
    },
    onError: () => {
      toast({
        title: "Erreur",
        description: "Impossible d'envoyer l'alerte panique",
        variant: "destructive",
      });
      setIsActivating(false);
    },
  });

  useEffect(() => {
    const resetTimer = setTimeout(() => {
      setPressCount(0);
    }, 1000);

    return () => clearTimeout(resetTimer);
  }, [lastPressTime]);

  const handlePanicPress = () => {
    // Un seul clic suffit pour activer la panique
    activatePanic();
  };

  const activatePanic = () => {
    // Don't allow activation while contacts are loading
    if (contactsLoading) {
      toast({
        title: "Chargement...",
        description: "Veuillez patienter pendant le chargement des contacts",
      });
      return;
    }

    if (!contacts || contacts.length === 0) {
      toast({
        title: "Aucun contact d'urgence",
        description: "Configurez vos contacts dans les paramètres",
        variant: "destructive",
      });
      return;
    }

    setIsActivating(true);
    panicMutation.mutate();
  };

  const handleSOSClick = () => {
    setLocation("/sos/publier");
  };

  return (
    <Card className="fixed bottom-20 md:bottom-24 right-2 md:right-8 z-50 overflow-hidden border-2 border-red-500/30 dark:border-red-400/30 shadow-2xl backdrop-blur-lg bg-gradient-to-br from-red-50/95 via-orange-50/95 to-red-50/95 dark:from-red-950/95 dark:via-orange-950/95 dark:to-red-950/95">
      <div className="p-2 md:p-4 space-y-2 md:space-y-3">
        {/* Header with status - hide on mobile */}
        <div className="hidden md:flex items-center justify-between">
          <div className="flex items-center gap-2">
            <Radio className="w-4 h-4 text-red-600 dark:text-red-400 animate-pulse" />
            <span className="text-sm font-semibold text-red-900 dark:text-red-100">
              Urgences
            </span>
          </div>
        </div>

        {/* Emergency Actions - Side by Side Layout */}
        <div className="flex gap-1.5 md:gap-2">
          {/* Panic Button */}
          <Button
            size="default"
            variant="destructive"
            className="flex-1 h-10 md:h-14 bg-gradient-to-br from-red-600 to-red-700 hover:from-red-700 hover:to-red-800 dark:from-red-700 dark:to-red-800 dark:hover:from-red-800 dark:hover:to-red-900 shadow-md border border-red-400/50 dark:border-red-500/50 transition-all duration-200 hover:scale-105 active:scale-95"
            onClick={handlePanicPress}
            disabled={isActivating || contactsLoading}
            data-testid="button-panic"
          >
            <div className="flex flex-col items-center gap-0.5">
              {contactsLoading ? (
                <>
                  <Loader2 className="w-3 h-3 md:w-4 md:h-4 animate-spin" />
                  <span className="text-[9px] md:text-[10px] font-bold">...</span>
                </>
              ) : isActivating ? (
                <>
                  <Loader2 className="w-3 h-3 md:w-4 md:h-4 animate-spin" />
                  <span className="text-[9px] md:text-[10px] font-bold">Envoi</span>
                </>
              ) : (
                <>
                  <AlertTriangle className="w-3 h-3 md:w-4 md:h-4" />
                  <span className="text-[9px] md:text-xs font-bold">PANIQUE</span>
                </>
              )}
            </div>
          </Button>

        {/* SOS Button */}
          <Button
            size="default"
            className="flex-1 h-10 md:h-14 bg-gradient-to-br from-orange-600 to-orange-700 hover:from-orange-700 hover:to-orange-800 dark:from-orange-700 dark:to-orange-800 dark:hover:from-orange-800 dark:hover:to-orange-900 text-white shadow-md border border-orange-400/50 dark:border-orange-500/50 transition-all duration-200 hover:scale-105 active:scale-95"
            onClick={handleSOSClick}
            data-testid="button-sos"
          >
            <div className="flex flex-col items-center gap-0.5">
              <AlertCircle className="w-3 h-3 md:w-4 md:h-4" />
              <span className="text-[9px] md:text-xs font-bold">SOS</span>
            </div>
          </Button>
        </div>

        {/* Info text - hide on mobile */}
        <p className="hidden md:block text-xs text-center text-red-800 dark:text-red-200 opacity-75">
          {contacts && contacts.length > 0 
            ? `${contacts.length} contact${contacts.length > 1 ? 's' : ''} d'urgence configuré${contacts.length > 1 ? 's' : ''}`
            : "Configurez vos contacts d'urgence dans votre profil"}
        </p>
      </div>
    </Card>
  );
}

client/src/pages/ProfilPublic.tsx.content:
import Header from "@/components/Header";
import BottomNav from "@/components/BottomNav";
import { Avatar, AvatarFallback, AvatarImage } from "@/components/ui/avatar";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Loader2, MapPin, Briefcase, Mail, Calendar } from "lucide-react";
import { useQuery } from "@tanstack/react-query";
import { useRoute } from "wouter";
import type { User, Signalement } from "@shared/schema";
import SignalementCard from "@/components/SignalementCard";

export default function ProfilPublic() {
  const [, params] = useRoute("/profil/:userId");
  const userId = params?.userId;

  const { data: user, isLoading: userLoading } = useQuery<User>({
    queryKey: [`/api/users/${userId}`],
    enabled: !!userId,
  });

  const { data: userSignalements = [], isLoading: signalementsLoading } = useQuery<Signalement[]>({
    queryKey: [`/api/users/${userId}/signalements`],
    enabled: !!userId,
  });

  if (userLoading) {
    return (
      <div className="flex items-center justify-center h-screen">
        <Loader2 className="w-8 h-8 animate-spin text-primary" />
      </div>
    );
  }

  if (!user) {
    return (
      <div className="min-h-screen bg-background pb-24">
        <Header />
        <div className="max-w-4xl mx-auto px-4 py-6">
          <p className="text-center text-muted-foreground">Utilisateur non trouvé</p>
        </div>
        <BottomNav />
      </div>
    );
  }

  const getInitials = () => {
    const first = user.firstName?.[0] || "";
    const last = user.lastName?.[0] || "";
    return (first + last).toUpperCase() || "U";
  };

  const calculateUserStats = () => {
    const validatedCount = userSignalements.filter(s => s.statut === 'resolu').length;
    const totalSignalements = userSignalements.length;
    const points = (validatedCount * 10) + (totalSignalements * 5);

    let badge = { icon: "🟢", label: "Citoyen Actif", color: "bg-green-100 text-green-800 border-green-300" };

    if (points >= 200) {
      badge = { icon: "🔴", label: "Ambassadeur Local", color: "bg-red-100 text-red-800 border-red-300" };
    } else if (points >= 100) {
      badge = { icon: "🟡", label: "Reporter Fiable", color: "bg-yellow-100 text-yellow-800 border-yellow-300" };
    } else if (points >= 50) {
      badge = { icon: "🔵", label: "Veilleur Régional", color: "bg-blue-100 text-blue-800 border-blue-300" };
    }

    return { points, badge };
  };

  const { points, badge } = calculateUserStats();

  return (
    <div className="min-h-screen bg-background pb-24">
      <Header />

      <div className="max-w-4xl mx-auto px-4 py-6">
        <Card className="mb-6">
          <CardContent className="p-6">
            <div className="flex flex-col md:flex-row items-start gap-6">
              <div className="relative">
                <Avatar className="w-32 h-32">
                  <AvatarImage src={user.profileImageUrl || undefined} style={{ objectFit: "cover" }} />
                  <AvatarFallback className="text-2xl">{getInitials()}</AvatarFallback>
                </Avatar>
                <div className="absolute -bottom-2 -right-2 text-4xl" title={badge.label}>
                  {badge.icon}
                </div>
              </div>

              <div className="flex-1">
                <h2 className="text-2xl font-bold mb-1">
                  {user.firstName || user.lastName
                    ? `${user.firstName || ""} ${user.lastName || ""}`.trim()
                    : "Utilisateur"}
                </h2>

                {user.ville && (
                  <div className="flex items-center gap-2 mb-2 text-muted-foreground">
                    <MapPin className="w-4 h-4" />
                    <span className="text-sm">{user.ville}</span>
                  </div>
                )}

                {user.metier && (
                  <div className="flex items-center gap-2 mb-2 text-muted-foreground">
                    <Briefcase className="w-4 h-4" />
                    <span className="text-sm">{user.metier}</span>
                  </div>
                )}

                {user.bio && (
                  <p className="text-sm mt-4">{user.bio}</p>
                )}

                <div className="mt-4 p-3 bg-gradient-to-r from-primary/10 to-primary/5 rounded-lg border border-primary/20">
                  <div className="flex items-center gap-3 mb-2">
                    <span className="text-3xl">{badge.icon}</span>
                    <div className="flex-1">
                      <Badge className={`${badge.color} border`}>
                        {badge.label}
                      </Badge>
                      <p className="text-sm text-muted-foreground mt-1">
                        <span className="font-bold text-primary">{points} points</span> au total
                      </p>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardHeader>
            <CardTitle>Publications ({userSignalements.length})</CardTitle>
          </CardHeader>
          <CardContent>
            {signalementsLoading ? (
              <div className="flex items-center justify-center py-8">
                <Loader2 className="w-6 h-6 animate-spin text-primary" />
              </div>
            ) : userSignalements.length === 0 ? (
              <div className="text-center py-8 text-muted-foreground">
                <p>Aucune publication pour le moment.</p>
              </div>
            ) : (
              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                {userSignalements.map((signalement) => (
                  <SignalementCard 
                    key={signalement.id} 
                    {...signalement}
                    createdAt={new Date(signalement.createdAt!)}
                  />
                ))}
              </div>
            )}
          </CardContent>
        </Card>
      </div>

      <BottomNav />
    </div>
  );
}

client/src/pages/Notifications.tsx.content:
import Header from "@/components/Header";
import BottomNav from "@/components/BottomNav";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Bell, AlertTriangle, CheckCircle, Info, ThumbsUp, MessageCircle } from "lucide-react";
import { Badge } from "@/components/ui/badge";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Button } from "@/components/ui/button";
import { formatDistanceToNow } from "date-fns";
import { fr } from "date-fns/locale";
import { Link } from "wouter";
import { useEffect } from "react";

export default function Notifications() {
  const queryClient = useQueryClient();

  const { data: notifications = [], isLoading } = useQuery({
    queryKey: ["/api/notifications"],
  });

  // Marquer automatiquement toutes les notifications comme lues à l'ouverture
  useEffect(() => {
    if (notifications.length > 0) {
      const unreadCount = notifications.filter((n: any) => !n.read).length;
      if (unreadCount > 0) {
        markAllAsReadMutation.mutate();
      }
    }
  }, [notifications.length]);

  const markAsReadMutation = useMutation({
    mutationFn: async (notificationId: string) => {
      const response = await fetch(`/api/notifications/${notificationId}/read`, {
        method: "PATCH",
        credentials: "include",
      });
      if (!response.ok) throw new Error("Failed to mark notification as read");
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/notifications"] });
      queryClient.invalidateQueries({ queryKey: ["/api/notifications/unread-count"] });
    },
  });

  const markAllAsReadMutation = useMutation({
    mutationFn: async () => {
      const response = await fetch("/api/notifications/mark-all-read", {
        method: "POST",
        credentials: "include",
      });
      if (!response.ok) throw new Error("Failed to mark all notifications as read");
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/notifications"] });
      queryClient.invalidateQueries({ queryKey: ["/api/notifications/unread-count"] });
    },
  });

  const getIcon = (type: string) => {
    switch (type) {
      case "urgence":
        return <AlertTriangle className="w-5 h-5 text-destructive" />;
      case "resolu":
        return <CheckCircle className="w-5 h-5 text-green-600" />;
      case "like":
        return <ThumbsUp className="w-5 h-5 text-blue-600" />;
      case "comment":
        return <MessageCircle className="w-5 h-5 text-purple-600" />;
      default:
        return <Info className="w-5 h-5 text-blue-600" />;
    }
  };

  const handleNotificationClick = (notification: any) => {
    if (!notification.read) {
      markAsReadMutation.mutate(notification.id);
    }
  };

  const unreadCount = notifications.filter((n: any) => !n.read).length;

  return (
    <div className="min-h-screen bg-background pb-24">
      <Header />

      <div className="max-w-4xl mx-auto px-4 py-6">
        <div className="mb-6 flex items-center justify-between">
          <div>
            <h1 className="text-3xl font-bold mb-2">Notifications</h1>
            <p className="text-muted-foreground">
              Restez informé des dernières mises à jour
            </p>
          </div>
          {unreadCount > 0 && (
            <Button
              variant="outline"
              size="sm"
              onClick={() => markAllAsReadMutation.mutate()}
              disabled={markAllAsReadMutation.isPending}
            >
              Tout marquer comme lu
            </Button>
          )}
        </div>

        {isLoading ? (
          <div className="flex items-center justify-center py-12">
            <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-primary"></div>
          </div>
        ) : (
          <div className="space-y-4">
            {notifications.map((notification: any) => (
              <Card
                key={notification.id}
                className={`cursor-pointer transition-colors hover:bg-accent ${
                  !notification.read ? "border-l-4 border-l-primary" : ""
                }`}
                onClick={() => handleNotificationClick(notification)}
                data-testid={`notification-${notification.id}`}
              >
                <CardHeader>
                  <div className="flex flex-wrap items-start justify-between gap-4">
                    <div className="flex items-start gap-3 flex-1">
                      {getIcon(notification.type)}
                      <div className="flex-1">
                        <div className="flex items-center gap-2 mb-1">
                          <CardTitle className="text-base">
                            {notification.title}
                          </CardTitle>
                          {!notification.read && (
                            <Badge variant="default" className="h-2 w-2 p-0 rounded-full" />
                          )}
                        </div>
                        <p className="text-sm text-muted-foreground">
                          {notification.description}
                        </p>
                        <p className="text-xs text-muted-foreground mt-2">
                          {formatDistanceToNow(new Date(notification.createdAt), {
                            addSuffix: true,
                            locale: fr,
                          })}
                        </p>
                        {notification.signalementId && (
                          <Link href={`/signalement/${notification.signalementId}`}>
                            <Button variant="link" size="sm" className="mt-2 p-0 h-auto">
                              Voir le signalement
                            </Button>
                          </Link>
                        )}
                      </div>
                    </div>
                  </div>
                </CardHeader>
              </Card>
            ))}

            {notifications.length === 0 && (
              <Card>
                <CardContent className="flex flex-col items-center justify-center py-12">
                  <Bell className="w-12 h-12 text-muted-foreground mb-4" />
                  <p className="text-muted-foreground text-center">
                    Aucune notification pour le moment
                  </p>
                </CardContent>
              </Card>
            )}
          </div>
        )}
      </div>

      <BottomNav />
    </div>
  );
}

client/src/components/SignalementCard.ts.content:
import { Card, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import CategoryBadge from "./CategoryBadge";
import StatutBadge from "./StatutBadge";
import EditSignalementDialog from "./EditSignalementDialog";
import CommentDialog from "./CommentDialog";
import type { Categorie, Statut, Signalement } from "@shared/schema";
import { Heart, Share2, MapPin, AlertCircle, ShieldCheck, ShieldAlert, Clock } from "lucide-react";
import { formatDistanceToNow } from "date-fns";
import { fr } from "date-fns/locale";
import { useState } from "react";
import { useAuth } from "@/hooks/useAuth";
import { useMutation } from "@tanstack/react-query";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import { Link } from "wouter";
import { Avatar, AvatarFallback, AvatarImage } from "@/components/ui/avatar";

interface SignalementCardProps {
  id: string;
  titre: string;
  description: string;
  categorie: string;
  localisation: string | null;
  latitude: string;
  longitude: string;
  photo?: string | null;
  isSOS?: boolean | null;
  isAnonymous?: boolean | null;
  niveauUrgence?: string | null;
  statut?: string | null;
  likes: number | null;
  commentairesCount?: number | null;
  sharesCount?: number | null;
  createdAt: Date;
  userId?: string | null;
  auteurFirstName?: string | null;
  auteurLastName?: string | null;
}

export default function SignalementCard({
  id,
  titre,
  description,
  categorie,
  localisation,
  latitude,
  longitude,
  photo,
  isSOS = false,
  isAnonymous = false,
  niveauUrgence = "moyen",
  statut = "en_attente",
  likes,
  commentairesCount = 0,
  sharesCount = 0,
  createdAt,
  userId,
  auteurFirstName,
  auteurLastName,
}: SignalementCardProps) {
  const [isLiked, setIsLiked] = useState(false);
  const [likeCount, setLikeCount] = useState(likes || 0);
  const [shareCount, setShareCount] = useState(sharesCount || 0);
  const { user, isAuthenticated } = useAuth();
  const { toast } = useToast();

  const auteur = isAnonymous 
    ? "Anonyme" 
    : (auteurFirstName || auteurLastName)
      ? `${auteurFirstName || ""} ${auteurLastName || ""}`.trim()
      : "Anonyme";

  const likeMutation = useMutation({
    mutationFn: async () => {
      const res = await apiRequest("POST", `/api/signalements/${id}/like`, {});
      return res.json();
    },
    onSuccess: (data) => {
      // Toggle the liked state
      const newIsLiked = !isLiked;
      setIsLiked(newIsLiked);

      // Update like count based on whether we're liking or unliking
      if (newIsLiked) {
        setLikeCount(prev => prev + 1);
      } else {
        setLikeCount(prev => Math.max(0, prev - 1));
      }

      queryClient.invalidateQueries({ queryKey: ["/api/signalements"] });
    },
    onError: () => {
      toast({
        title: "Erreur",
        description: "Impossible de modifier le like",
        variant: "destructive",
      });
    },
  });

  const handleLike = () => {
    if (!isAuthenticated) {
      toast({
        title: "Connexion requise",
        description: "Vous devez être connecté pour aimer un signalement",
        variant: "destructive",
      });
      window.location.href = "/api/login";
      return;
    }
    likeMutation.mutate();
  };

  const shareMutation = useMutation({
    mutationFn: async () => {
      const res = await apiRequest("POST", `/api/signalements/${id}/share`, {});
      return res.json();
    },
    onSuccess: (data) => {
      setShareCount(data.sharesCount || 0);
      queryClient.invalidateQueries({ queryKey: ["/api/signalements"] });
    },
    onError: () => {
      toast({
        title: "Erreur",
        description: "Impossible d'enregistrer le partage",
        variant: "destructive",
      });
    },
  });

  const handleShare = async () => {
    const shareData = {
      title: titre,
      text: description,
      url: `${window.location.origin}/signalement/${id}`,
    };

    if (navigator.share && navigator.canShare?.(shareData)) {
      try {
        await navigator.share(shareData);
        shareMutation.mutate();
        toast({
          title: "Partagé",
          description: "Le signalement a été partagé avec succès",
        });
      } catch (error: any) {
        if (error.name !== "AbortError") {
          handleCopyLink();
        }
      }
    } else {
      handleCopyLink();
    }
  };

  const handleCopyLink = () => {
    const url = `${window.location.origin}/signalement/${id}`;
    navigator.clipboard.writeText(url).then(() => {
      shareMutation.mutate();
      toast({
        title: "Lien copié",
        description: "Le lien du signalement a été copié dans le presse-papiers",
      });
    }).catch(() => {
      toast({
        title: "Erreur",
        description: "Impossible de copier le lien",
        variant: "destructive",
      });
    });
  };

  const canEdit = userId === "demo-user" || (user && userId && user.id === userId);

  const handleOpenGoogleMaps = () => {
    if (localisation) {
      const mapsUrl = `https://www.google.com/maps/search/?api=1&query=${encodeURIComponent(localisation)}`;
      window.open(mapsUrl, '_blank', 'noopener,noreferrer');
    } else if (latitude && longitude) {
      const mapsUrl = `https://www.google.com/maps/search/?api=1&query=${latitude},${longitude}`;
      window.open(mapsUrl, '_blank', 'noopener,noreferrer');
    }
  };

  return (
    <Card className={`overflow-hidden ${isSOS ? "border-2 border-category-urgence" : ""}`}>
      {photo && (
        <div className="relative w-full h-48">
          <img
            src={photo}
            alt={titre}
            className="w-full h-full object-cover"
          />
          <div className="absolute top-2 right-2 flex gap-2">
            <CategoryBadge categorie={categorie as Categorie} />
            {isSOS && (
              <span className="flex items-center gap-1 bg-category-urgence text-white px-3 py-1 rounded-full text-xs font-semibold animate-pulse">
                <AlertCircle className="w-3 h-3" />
                SOS
              </span>
            )}
          </div>
        </div>
      )}
      <CardContent className="p-4">
        {!photo && (
          <div className="flex flex-wrap gap-2 mb-3">
            <CategoryBadge categorie={categorie as Categorie} />
            {isSOS && (
              <span className="flex items-center gap-1 bg-category-urgence text-white px-3 py-1 rounded-full text-xs font-semibold animate-pulse">
                <AlertCircle className="w-3 h-3" />
                SOS
              </span>
            )}
          </div>
        )}

        <Link href={`/signalement/${id}`}>
          <h3 className="text-lg font-semibold mb-2 line-clamp-2 hover:text-primary cursor-pointer transition-colors" data-testid={`link-signalement-${id}`}>
            {titre}
          </h3>
        </Link>
        <p className="text-sm text-muted-foreground mb-2 line-clamp-2">
                {description}
              </p>

              {userId && auteur !== "Anonyme" && (
                <Link href={`/profil/${userId}`}>
                  <div className="flex items-center gap-2 mb-2 hover:opacity-80 cursor-pointer">
                    <Avatar className="w-6 h-6">
                      <AvatarImage src="" />
                      <AvatarFallback className="text-xs">
                        {auteurFirstName?.[0]}{auteurLastName?.[0]}
                      </AvatarFallback>
                    </Avatar>
                    <span className="text-xs text-muted-foreground">
                      {auteur}
                    </span>
                  </div>
                </Link>
              )}

        <button
          onClick={handleOpenGoogleMaps}
          className="flex items-center gap-2 text-sm text-muted-foreground mb-3 hover-elevate active-elevate-2 rounded px-2 py-1 -ml-2 transition-colors"
          data-testid={`button-location-${id}`}
        >
          <MapPin className="w-4 h-4 flex-shrink-0" />
          <span className="line-clamp-1">{localisation || "Localisation inconnue"}</span>
        </button>

        <div className="flex flex-wrap items-center justify-between gap-2 mb-3">
          <div className="flex items-center gap-2">
            <StatutBadge statut={(statut || "en_attente") as Statut} />
          </div>
          <span className="text-xs text-muted-foreground">
            {formatDistanceToNow(createdAt, { addSuffix: true, locale: fr })}
          </span>
        </div>

        {canEdit && (
          <div className="mb-3">
            <EditSignalementDialog 
              signalement={{
                id,
                titre,
                description,
                categorie,
                localisation,
                latitude,
                longitude,
                photo,
                video: null,
                isSOS,
                isAnonymous,
                niveauUrgence,
                statut,
                likes,
                userId,
                createdAt,
              } as Signalement}
            />
          </div>
        )}

        <div className="flex items-center gap-1 pt-3 border-t">
          <Button
            variant="ghost"
            size="sm"
            className={`flex items-center gap-1 ${isLiked ? "text-destructive" : ""}`}
            onClick={handleLike}
            disabled={likeMutation.isPending}
            data-testid={`button-like-${titre}`}
          >
            <Heart className={`w-4 h-4 ${isLiked ? "fill-current" : ""}`} />
            <span className="text-xs">{likeCount}</span>
          </Button>
          <CommentDialog signalementId={id} commentCount={commentairesCount || 0} />
          <Button
            variant="ghost"
            size="sm"
            className="flex items-center gap-1"
            onClick={handleShare}
            data-testid={`button-share-${titre}`}
          >
            <Share2 className="w-4 h-4" />
            <span className="text-xs">{shareCount}</span>
          </Button>
        </div>
      </CardContent>
    </Card>
  );
}

