  icon: LucideIcon;
  description?: string;
  variant?: "default" | "destructive" | "success" | "info";
  trend?: "up" | "down" | "neutral";
}

export default function StatCard({
  title,
  value,
  icon: Icon,
  description,
  variant = "default",
  trend = "neutral"
